INSERT INTO `books` VALUES ('一句顶一万句', 8533, '\n\n\n刘震云', '小说', '长江文艺出版社', '《一句顶一万句》的故事很简单，小说的前半部写的是过去：孤独无助的吴摩西失去唯一能够“说得上话”的养女，为了寻找，走出延津；小说的后半部写的是现在：吴摩西养女... ', '2009-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('克莱因壶', 8534, '\n\n\n[日]冈岛二人张舟', '小说', '化学工业出版社', '什么时候你开始怀疑这个世界是假的？\n.\n日本虚拟现实VR题材开山杰作，\n超前《盗梦空间》20年！\n传奇推理作家组合冈岛二人预言之书，以超强的技术预见力，30... ', '2019-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('新名字的故事', 8535, '\n\n\n[意]埃莱娜·费兰特陈英', '小说', '人民文学出版社', '只有你身为女人 才会知道这些丑陋的秘密\n两个女人 50年的友谊和战争\n如何成为那个更强大的她，又不被她战胜\n那不勒斯四部曲NO.2\n《我的天才女友》后续， ... ', '2017-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('失踪的孩子', 8536, '\n\n\n[意]埃莱娜·费兰特陈英', '小说', '人民文学出版社', '“我的整个生命，只是一场为了提升社会地位的低俗斗争。”\n全球畅销近千万册被翻译成40多种语言\n《我的天才女友》《新名字的故事》《离开的，留下的》——\n那不勒... ', '2018-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('晚婚', 8537, '\n\n\n辽京', '小说', '中信出版集团', '【编辑推荐】\n◆ 不安的心是无底的洞，幸福只填得满一半！\n“眼光不要太高啊”“谈恋爱这么久了，怎么还不提结婚”“结婚有那么可怕吗”\n刚过30岁,年龄、婚姻、... ', '2021-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('卡拉马佐夫兄弟', 8538, '\n\n\n[俄]费奥多尔', '小说', '上海译文出版社', '老卡拉马佐夫贪婪好色，独占妻子留给儿子们的遗产，并与长子德米特里为一个风流女子争风吃醋。一天黑夜，德米特里疑心自己的情人去跟老头儿幽会，便闯入家园，一怒之下... ', '2006-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('老人与海', 8539, '\n\n\n海明威吴劳', '小说', '上海译文出版社', '本书讲述了一个渔夫的故事。古巴老渔夫圣地亚哥在连续八十四天没捕到鱼的情况下，终于独自钓上了一条大马林鱼，但这鱼实在大，把他的小船在海上拖了三天才筋疲力尽，被... ', '1999-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('夏天、烟火和我的尸体', 8540, '\n\n\n[日]乙一连子心', '小说', '南海出版公司', '★ 乙一惊世成名作，16岁写成，轰动文坛。\n★ 九岁那年的夏天，我看着我的尸体漫山遍野奔跑。\n★ 一部书写天真与恶的暗黑杰作。这个盛夏的故事，让人冷到骨子里... ', '2019-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('孽子', 8541, '\n\n\n白先勇', '小说', '广西师范大学出版社', '白先勇唯一的一部长篇小说《孽子》，以同性恋为题材，全书共分为四章，依次是“放逐”、“在我们的王国里”、“安乐乡”、“那些青春鸟的行旅”，描写了上个世纪六七十... ', '2010-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('司藤', 8542, '\n\n\n尾鱼', '小说', '江苏文艺出版社', '1946年，天师道长丘山于沪上镇杀女妖司藤，临死前，司藤嘴角现出一抹如释重负的诡异微笑。\n2013年，男子秦放携未婚妻前往西部囊千寻找一位祖上的恩人，车毁坠... ', '2015-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('岛上书店', 8543, '\n\n\n[美]加布瑞埃拉·泽文', '小说', '江苏凤凰文艺出版社', '每个人的生命中，都有最艰难的那一年，将人生变得美好而辽阔。\n---------------------\nA．J．费克里，人近中年，在一座与世 隔绝的小岛上，... ', '2015-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('一个陌生女人的来信', 8544, '\n\n\n[奥]斯蒂芬·茨威格张玉书', '小说', '上海译文出版社', '这是一部短篇小说集，除《一个陌生女人的来信》，亦按时间顺序收录了《火烧火燎的秘密》、《马来狂人》等名篇，作者的创作历程一目了然。\n《一个陌生女人的来信》讲述... ', '2007-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('喀耳刻', 8545, '\n\n\n[美]马德琳·米勒', '小说', '中信出版集团', '我拥有别人认为我不该有的欲望、叛逆和力量。\n我拥有会引雷霆上身的一切。\n我也会劈开这一切！\n----------\n【内容简介】\n神明、女巫、母亲、凡人\n无视... ', '2021-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('嫌疑人X的献身', 8546, '\n\n\n[日]东野圭吾刘子倩', '小说', '南海出版公司', '百年一遇的数学天才石神，每天唯一的乐趣，便是去固定的便当店买午餐，只为看一眼在便当店做事的邻居靖子。\n靖子与女儿相依为命，失手杀了前来纠缠的前夫。石神提出由... ', '2014-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('在细雨中呼喊', 8547, '\n\n\n余华', '小说', '作家出版社', '《在细雨中呼喊》是二十世纪九十年代的经典文本，是余华的第一部长篇力作。小说描述了一位江南少年的成长经历和心灵历程。《在细雨中呼喊 》的结构来自于对时间的感受... ', '2012-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('候场', 8548, '\n\n\n李诞', '小说', '上海文艺出版社', '《候场》是李诞在2020年初完成的一部中篇小说。这本书里，李诞写了一个叫“李诞”的人的故事。“一个在迅速扩张的大时代，一个懵懵懂懂踏入名利场的年轻人，度过激... ', '2020-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('人生', 8549, '\n\n\n路遥', '小说', '北京十月文艺出版社', '《人生》是路遥的一部中篇小说，发表于1982年，它以改革时期陕北高原的城乡生活为时空背景，叙述了高中毕业生高加林回到土地又离开土地，再回到土地这样人生的变化... ', '2009-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('受命', 8550, '\n\n\n止庵', '小说', '人民文学出版社', '《受命》是止庵的首部长篇小说，也是一部致敬《史记•伍子胥列传》和《哈姆雷特》的小说。这部作品关乎记忆和爱情，记忆来自既往，还没有退场；爱情指向新生，却无法生... ', '2021-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('肖申克的救赎', 8551, '\n\n\n[美]斯蒂芬·金', '小说', '人民文学出版社', '这本书收入斯蒂芬·金的四部中篇小说，是他作品中的杰出代表作。其英文版一经推出，即登上《纽约时报》畅销书排行榜的冠军之位，当年在美国狂销二十八万册。目前，这本... ', '2006-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('回归故里', 8552, '\n\n\n[法]迪迪埃·埃里蓬王献', '小说', '上海文化出版社', '“我们来到这个世界时，命运是否早已被宣判？”\n.\n当代著名思想家、《米歇尔·福柯传》作者\n迪迪埃·埃里蓬切身之作\n出版后即在《世界报》《自由报》等知名报刊引... ', '2020-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('繁花', 8553, '\n\n\n金宇澄', '小说', '上海文艺出版社', '这是一部地域小说，人物的行走，可找到“有形”地图的对应。这也是一部记忆小说，六十年代的少年旧梦，辐射广泛，处处人间烟火的斑斓记忆，九十年代的声色犬马，是一场... ', '2013-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('关于那个人的备忘录', 8554, '\n\n\n[日]小林泰三赵滢', '小说', '新星出版社', '◎脑子里的记忆可信，还是写下来的文字可信？\n◎电波系推理小说  超能力与反超能力\n看完整本书后再翻回开头，或许会感到脊背发凉。\n--------------... ', '2021-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('长夜难明', 8555, '\n\n\n紫金陈', '小说', '云南人民出版社', '麦家、鹦鹉史航、马伯庸、雷米、周浩晖都推崇的作家\n社会派悬疑推理大神紫金陈“推理之王”系列第3部\n改编网络剧《沉默的真相》，廖凡、白宇主演，即将重磅上线\n第... ', '2017-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('平原上的摩西', 8556, '\n\n\n双雪涛', '小说', '百花文艺出版社', '本书是双雪涛的第一部中短篇小说集，书中收录了10篇作品，小说风格不一，题材有校园成长类，奇人故事类，侦探悬疑类，作者的笔调朴素，冷峻又有文字表面按捺不住的恣... ', '2016-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('局外人', 8557, '\n\n\n(法)阿尔贝·加缪柳鸣九', '小说', '上海译文出版社', '阿尔贝•加缪（1913—1960）是法国声名卓著的小说家、散文家和剧作家，“存在主义”文学的大师。1957年因“热情而冷静地阐明了当代向人类良知提出的种种问... ', '2013-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('面纱', 8558, '\n\n\n(英)W.萨默塞特·毛姆阮景林', '小说', '重庆', ' 《面纱》由W.萨默塞特·毛姆编著。\n《面纱》讲述了：容貌娇美而又爱慕虚荣的英国女子凯蒂，为了避免自己变成一位老姑娘，接受了生性孤僻的医生瓦尔特·费恩的求婚... ', '2012-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('雪国', 8559, '\n\n\n川端康成叶渭渠、唐月梅', '小说', '南海出版公司', '史上最美诺贝尔文学奖作品，教育部推荐大学生必读书目\n大师经典名作，全新精装收藏版\n像《百年孤独》一样，《雪国》影响了余华、莫言、贾平凹等几代中国作家\n川端康... ', '2013-09-15', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('窗边的小豆豆', 8560, '\n\n\n[日]黑柳彻子著、岩崎千弘图', '小说', '南海出版公司', '《窗边的小豆豆》讲述了作者上小学时的一段真实的故事。作者因淘气被原学校退学后，来到巴学园。在小林校长的爱护和引导下，让一般人眼里“怪怪”的小豆豆逐渐成了一个... ', '2003-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('正常人', 8561, '\n\n\n[爱尔兰]萨莉·鲁尼钟娜', '小说', '上海译文出版社', '★ 风靡全球的电视剧 Normal People 原著\n★ 九零后爱尔兰女作家，英国图书奖、科斯塔图书奖有史以来最年轻的获奖者萨莉•鲁尼的代表作\n★ 201... ', '2020-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('穆斯林的葬礼', 8562, '\n\n\n霍达', '小说', '北京十月文艺出版社', '一个穆斯林家族，六十年间的兴衰，三代人命运的沉浮，两个发生在不同时代、有着不同内容却又交错扭结的爱情悲剧。\n这部五十余万字的长篇，以独特的视角，真挚的情感，... ', '1988-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('风声', 8563, '\n\n\n麦家', '小说', '上海文艺出版社', '茅盾文学奖得主麦家代表作《风声》，2020年全新修订版本\n.\n风声大作，震撼上市！\n=========\n【编辑推荐】\n◆经历过大孤独、大绝望的人，会懂得《风... ', '2020-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('小径分岔的花园', 8564, '\n\n\n[阿根廷]豪·路·博尔赫斯王永年', '小说', '上海译文出版社', '本书为1941年的短篇小说集，收小说七篇。其中，《小径分岔的花园》是侦探小说，讲述一桩罪行的准备工作和实施过程。小径分岔的花园是一个谜语，或者说寓言，而谜底... ', '2015-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('卡拉马佐夫兄弟', 8565, '\n\n\n陀思妥耶夫斯基', '小说', '上海译文出版社', '本书系俄国大作家陀思妥耶夫斯基代表作之一。小说通过一桩真实的弑父案，描写老卡拉马佐夫同三个儿子即两代人之间的尖锐冲突。老卡拉马佐夫贪婪好色，独占妻子留给儿子... ', '2015-02-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('失明症漫记', 8566, '\n\n\n[葡]若泽·萨拉马戈范维信', '小说', '南海出版公司', '你为什么会写这样一部冷酷的作品？\n虽然我活得很好，但这个世界却不好。\n----------------------------------------\n诺贝... ', '2014-02-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('安娜·卡列尼娜', 8567, '\n\n\n[俄]列夫·托尔斯泰草婴', '小说', '上海文艺出版社', '《安娜·卡列尼娜》是托尔斯泰第二部里程碑式的长篇小说，创作于 1873—1877年。作品由两条既平行又相互联系的线索构成：一条是安娜与卡列宁、伏伦斯基之间的... ', '2007-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('晚熟的人', 8568, '\n\n\n莫言', '小说', '人民文学出版社', '内容简介：\n诺贝尔文学奖得主  莫言\n获奖后首部作品\n十年蕴积，人事全新。\n一言掷地，壁立千仞。\n\n莫言的想象飞越于整个人类的存在状态之上。\n——诺贝尔文学... ', '2020-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('台北人', 8569, '\n\n\n白先勇', '小说', '广西师范大学出版社', '作为20世纪中文小说100强的《台北人》，是一部深具复杂性的短篇小说集，由十四个一流的短篇小说构成，串联成一体，则效果遽然增加，不但小说之幅面变广，使我们看... ', '2010-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('无人生还', 8570, '\n\n\n[英]阿加莎・克里斯蒂祁阿红', '小说', '人民文学出版社', '十个互不相识的人，被富有的欧文先生邀请到了印地安岛上的私人别墅里。晚餐后，一个神秘的声音揭开了人们心中所各自隐藏着的可怕秘密。当天晚上，年轻的马斯顿先生离奇... ', '2008-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('西线无战事', 8571, '\n\n\n[德]埃里希·玛丽亚·雷马克姜乙', '小说', '上海文艺出版社', '“我们才刚开始热爱生活，却不得不对这一切开炮。”\n《悉达多》《人类群星闪耀时》译者、翻译家姜乙新译作，1929年德语原版直译。\n6000字译后记，特别收录雷... ', '2021-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('一个叫欧维的男人决定去死', 8572, '\n\n\n[瑞典]弗雷德里克·巴克曼宁蒙', '小说', '四川文艺出版社', '来认识一下欧维，这个59岁的老头，脾气古怪，带着坚不可摧的原则、每天恪守的常规以及随时发飙的脾性在社区晃来晃去，被背地里称为“地狱来的恶邻”。他每天一大早就... ', '2015-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('罗杰疑案', 8573, '\n\n\n[英]阿加莎·克里斯蒂常禾', '小说', '新星出版社', '金斯艾伯特村里坐落着两座豪宅，“皇家围场”是其中之一，而居住其中的弗拉尔斯太太刚刚过世。\n此后不久，她的情人，也就是住在另一所大宅里的罗杰•艾克罗伊德先生便... ', '2013-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('不能承受的生命之轻', 8574, '\n\n\n[捷克]米兰·昆德拉许钧', '小说', '上海译文出版社', '《不能承受的生命之轻》是米兰·昆德拉最负盛名的作品。小说描写了托马斯与特丽莎、萨丽娜之间的感情生活。但它不是一个男人和两个女人的三角性爱故事，它是一部哲理小... ', '2003-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('尘埃落定', 8575, '\n\n\n阿来', '小说', '人民文学出版社', '一个声势显赫的康巴藏族土司，在酒后和汉族太太生了一个傻瓜儿子。这个人人都认定的傻子与现实生活格格不入，却有着超时代的预感和举止，成为土司制度兴衰的见证人。小... ', '1998-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('平面国', 8576, '\n\n\n[英]埃德温·A·艾勃特鲁冬旭', '小说', '上海文化出版社', '很难简单概括《平面国》这本书。\n它可以是：首次解释\"维度\"概念的科普读物、脑洞大开的科幻小说、煞有介事的数学童话、剑走偏锋的精神启蒙、辛辣讽喻英国维多利亚时... ', '2020-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('外婆的道歉信', 8577, '\n\n\n[瑞典]弗雷德里克·巴克曼孟汇一', '小说', '天津人民出版社', '2016年瑞典年度作家 美国亚马逊作家排名No.1\n版权售出40国 遍布欧美几乎每家书店\n上市10个月 全球总销量突破150万册\n出版后持续霸占《纽约时报》... ', '2017-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('烧纸', 8578, '\n\n\n[韩]李沧东金冉', '小说', '武汉大学出版社', '♦韩国电影大师、作家型导演李沧东小说代表作。\n♦心怀悲悯的现实主义写作，李沧东的一切电影创作的源头。\n♦琐碎的生活暗藏汹涌洪流，直指生命深处的被掩藏、遗忘的... ', '2020-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('包法利夫人', 8579, '\n\n\n福楼拜李健吾', '小说', '人民文学出版社', '《包法利夫人》于1856-1857年间在《巴黎杂志》上连载，轰动文坛，在社会上引起轩然大波。法当局对作者提起公诉，指控小说“伤风败俗、亵渎宗教”，并传唤作者... ', '2003-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('东方快车谋杀案', 8580, '\n\n\n[英]阿加莎·克里斯蒂陈尧光', '小说', '人民文学出版社', '午夜过后，一场大雪迫使东方快车停了下来。这辆豪华列车整年都处于满员状态。但那天早上却发现少了一名乘客。一个美国人死在了他的包厢里，他被刺了十二刀，可他包厢的... ', '2006-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('草房子', 8581, '\n\n\n曹文轩', '小说', '江苏少年儿童出版社', '这是一部讲究品位的少年长篇小说。\n作品写了男孩桑桑刻骨铭心、终身难忘的六年小学生活。六年中，他亲眼目睹或直接参与了一连串看似寻常但又催人泪下、撼动人心的故事... ', '2009-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('天龙八部', 8582, '\n\n\n金庸', '小说', '生活·读书·新知三联书店', '天龙八部乃金笔下的一部长篇小说，与《射雕》，《神雕》等 几部长篇小说一起被称为可读性最高的金庸小说。《天龙》的故事情节曲折，内容丰富，也曾多次被改编为电视作... ', '1994-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('西游记（全二册）', 8583, '\n\n\n吴承恩黄肃秋注释', '小说', '人民文学出版社', '《西游记》主要描写的是孙悟空保唐僧西天取经，历经九九八十一难的故事。唐僧取经是历史上一件真实的事。大约距今一千三百多年前，即唐太宗贞观元年（627），年仅2... ', '2004-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('破云', 8584, '\n\n\n淮上', '小说', '江苏凤凰文艺出版社', '城市天空，诡云奔涌。\n三年前恭州市的缉毒行动中，因总指挥江停判断失误，现场发生连环爆炸，禁毒支队伤亡惨重。\n三年后，本应早已因过殉职并尸骨无存的江停，竟奇迹... ', '2019-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('鸳鸯六七四', 8585, '\n\n\n马家辉', '小说', '花城出版社', '“每个人都可能会摸到烂牌，把手上的烂牌打好，是我们一生唯一能做的事情。\n==========================================... ', '2020-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('罪与罚', 8586, '\n\n\n[俄]陀思妥耶夫斯基岳麟', '小说', '上海译文出版社', '《罪与罚》系19世纪俄国大文豪陀思妥耶夫斯基的代表作之一。小说描写一心想成为拿破仑式的人物、认定自己是个超人的穷大学生拉斯柯尔尼科夫，受无政府主义思想毒害，... ', '2006-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('彷徨', 8587, '\n\n\n鲁迅', '小说', '人民文学出版社', '《彷徨》收入鲁迅1924年至1925年所作的小说，首篇《祝福》写于1924年2月16日，末篇《离婚》写于1925年11月6日，实际的时间跨度是一年半多，19... ', '1973-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('某某', 8588, '\n\n\n木苏里', '小说', '江苏凤凰文艺出版社', '十七岁那年，蝉鸣声响，白马梧桐。\n十七岁少年，轻狂奔跑，仲夏荒原。\n他们发着光，也流着汗。\n他们无坚不摧，也无所不能。\n烈日骄阳，并肩穿行。\n长风吹过，野草... ', '2020-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('夜行货车', 8589, '\n\n\n陈映真', '小说', '九州出版社', '【编辑推荐】\n★20世纪文学大家陈映真小说全集大陆首次出版，温馨深沉的人间爱，理想主义者的灵魂哀歌。《夜行货车》是陈映真“华盛顿大楼”系列经典，台北八〇年代... ', '2020-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('那个不为人知的故事', 8590, '\n\n\ntwentine', '小说', '江苏文艺出版社', '我曾拥有一段时光\n在那段时光里\n我能用我贫瘠的词语描绘出每一分每一秒\n我能用我枯竭的心灵记住所有的细节\n但这段时光很短暂\n就像一个故事刚刚有了开篇就戛然而止... ', '2015-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('第七天', 8591, '\n\n\n余华', '小说', '新星出版社', '名人推荐 媒体推荐 作者简介 目录 文摘\n编辑推荐\n《第七天》获华语文学传媒大奖·年度杰出作家。温暖与冷漠、良善与邪恶、真实与荒诞、实有与虚无，交织成一部时... ', '2018-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('持续做一个深情的人', 8592, '\n\n\n林清玄', '小说', '北京时代华文书局', '情深，万象皆深。心扉的突然洞开，来自从容，来自有情。\n《持续做一个深情的人》是林清玄先生以情感为主题的美文随笔。书中有林先生对一朵花、一杯茶，一场清风的深情... ', '2018-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我本不该成为母亲', 8593, '\n\n\n[加拿大]阿什莉·奥德兰李雅欣', '小说', '中信出版社', '上一代破碎的关系是否会以某种方式延续？一个活着但冷漠的孩子，一个温暖却死去的孩子。母职焦虑，信任崩溃，亲情断裂……成为母亲，究竟面临多少困境？\n为什么幼儿园... ', '2021-01-07', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('动物农场', 8594, '\n\n\n[英]乔治·奥威尔荣如德', '小说', '上海译文出版社', '《动物农场》是奥威尔最优秀的作品之一，是一则入木三分的反乌托的政治讽喻寓言。农场的一群动物成功地进行了一场“革命”，将压榨他们的人类东家赶出农场，建立起一个... ', '2010-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('离开的，留下的', 8595, '\n\n\n[意]埃莱娜·费兰特陈英', '小说', '人民文学出版社', '两个女人 50年的友谊和战争\n那不勒斯四部曲NO.3\n全球畅销近千万册 被翻译成40多种语言\n“我希望她在场，这是我写作的目的。”\n《我的天才女友》《新名字... ', '2017-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('聋哑时代', 8596, '\n\n\n双雪涛', '小说', '广西师范大学出版社', '“小学毕业的时候，是1997年的夏天，和之后每一次毕业一样，炎热而干燥。”\n从那个时候开始，关于一切痛苦和欢愉，我都准备好了。\n90年代末的东北小城，那个外... ', '2020-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('无人生还', 8597, '\n\n\n[英]阿加莎·克里斯蒂夏阳', '小说', '新星出版社', '十个素不相识、身份各异的人受邀前往德文郡海岸边一座孤岛上的豪宅。客人到齐后，主人却没有出现。\n当晚，一个神秘的声音发出指控，分别说出每个人心中罪恶的秘密。接... ', '2013-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('莫失莫忘', 8598, '\n\n\n[英]石黑一雄张坤', '小说', '上海译文出版社', '★ 2017诺奖得主石黑一雄迄今为止最感人的作品\n★ 一部笔触细腻、技艺娴熟的经典巨作\n★ 一场史无前例的情感体验 一部生物工程时代的《一九八四》\n★ 20... ', '2018-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('福尔摩斯探案全集诺顿注释本 第一卷', 8599, '\n\n\n[英]阿瑟·柯南·道尔著', '小说', '浦睿文化·湖南文艺出版社', '对福尔摩斯和他所处时代的权威诠释。\n——《纽约时报》\n著名福学家莱斯利·克林格花费近30年编著的福尔摩斯注释本，荣获爱伦·坡奖、“特别歇洛克”奖等多项大奖，... ', '2021-02-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('活着', 8600, '\n\n\n余华', '小说', '作家出版社', '《活着》简介：地主少爷福贵嗜赌成性，终于赌光了家业，一贫如洗。穷困之中的福贵因为母亲生病前去求医，没想到半路上被国民党部队抓了壮丁，后被解放军所俘虏，回到家... ', '2010-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('醉步男', 8601, '\n\n\n[日]小林泰三丁丁虫', '小说', '北京时代华文书局', '本书收录时间旅行小说《醉步男》+日式恐怖小说《玩具修理者》。\n——————————————\n血沼，我以后大概都不会和你相遇了。\n话没说完，小竹田在我眼前消失... ', '2019-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('一桩事先张扬的凶杀案', 8602, '\n\n\n[哥伦比亚]加西亚·马尔克斯', '小说', '南海出版公司', '★马尔克斯自认为掌控得最好的作品。\n★这是一个残忍的犯罪故事，一部极具张力的小说，为此我写了三十年。——加西亚•马尔克斯\n★它综合了我以往所有作品的元素，我... ', '2013-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('全球高考', 8603, '\n\n\n木苏里', '小说', '北京燕山出版社', '用于体能筛选训练的人工智能系统突然有了自主意识，一切事情开始超出控制范畴。主角游惑被失控的系统拉进了一场虚拟“考试”里，一并被拉进来的还有各色各样的普通人，... ', '2020-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('莫斯科绅士', 8604, '\n\n\n[美]埃默·托尔斯马韧', '小说', '湖南文艺出版社', '【编辑推荐】\n◆一个人如果不能掌控自己的命运，就会被命运掌控。\n◆美国前总统奥巴马、奥斯卡影帝汤姆•汉克斯、比尔·盖茨年度推荐。\n◆都柏林文学奖入围作品。《... ', '2018-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('灿烂千阳', 8605, '\n\n\n[美]卡勒德·胡赛尼李继宏', '小说', '上海人民出版社', '私生女玛丽雅姆在父亲的宅院门口苦苦守候，回到家却看到因绝望而上吊自杀的母亲。那天是她十五岁的生日，而童年嘎然而止。玛丽雅姆随后由父亲安排远嫁喀布尔四十多岁的... ', '2007-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('往复书简：初恋与不伦', 8606, '\n\n\n[日]坂元裕二蕾克', '小说', '北京联合出版公司', '✉【编辑推荐】\n是枝裕和、椎名林檎、满岛光推崇的日剧大神，《东京爱情故事》《四重奏》编剧坂元裕二唯一心碎小说集首度引进；\n写给孤独者的深情书简，致艰难生存的... ', '2020-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('情人', 8607, '\n\n\n[法]玛格丽特·杜拉斯王道乾', '小说', '上海译文出版社', '杜拉斯代表作之一，自传性质的小说，获一九八四年法国龚古尔文学奖。全书以法国殖民者在越南的生活为背景，描写贫穷的法国女孩与富有的中国少爷之间深沉而无望的爱情。... ', '2005-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('半生缘', 8608, '\n\n\n张爱玲', '小说', '北京十月文艺出版社', '《半生缘》讲的是三十年代上海的一个悲惨的爱情故事。女主人公顾曼桢家境贫寒，自幼丧父，老小七人全靠姐姐曼璐做舞女养活。曼桢毕业后在一家公司工作，与来自南京的沈... ', '2012-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('一九八四', 8609, '\n\n\n[英]乔治·奥威尔董乐山', '小说', '上海译文出版社', '《一九八四》（1949）是奥威尔的传世之作，堪称世界文坛最著名的反乌托邦、反极权的政治讽喻小说。他在小说中创造的“老大哥”、“双重思想”、“新话”等词汇都已... ', '2009-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('偷影子的人', 8610, '\n\n\n(法)马克·李维段韵灵', '小说', '湖南文艺出版社', '一个老是受班上同学欺负的瘦弱小男孩，因为拥有一种特殊能力而强大：他能“偷别人的影子”，因而能看见他人心事，听见人们心中不愿意说出口的秘密。他开始成为需要帮助... ', '2012-06-20', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('麦田里的守望者', 8611, '\n\n\n[美国]J.D.塞林格孙仲旭', '小说', '译林出版社', '这本小说一出版，就受到国内青少年的热烈欢迎，认为它道出了自己的心声，一时大、中学校的校园里到处都模仿小说主人公霍尔顿——他们在大冬天身穿风衣，倒戴着红色猎人... ', '2007-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('江南三部曲', 8612, '\n\n\n格非', '小说', '北京十月文艺出版社', '★ 第九届茅盾文学奖最高票获奖作品\n《人面桃花》《山河入梦》《春尽江南》三部曲正式合体，全新修订典藏版\n★ 格非代表作，从构思到定稿，历时十七年\n★接受生活... ', '2019-08-05', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('永恒的终结', 8613, '\n\n\n[美]艾萨克·阿西莫夫崔正男', '小说', '江苏凤凰文艺出版社', '24世纪，人类发明了时间力场。27世纪，人类在掌握时间旅行技术后，成立了一个叫做永恒时空（Eternity）的组织，在每个时代的背后，默默地守护着人类社会的... ', '2014-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('鲁滨孙历险记', 8614, '\n\n\n笛福黄杲炘', '小说', '上海译文出版社', '《鲁滨孙历险记》中鲁滨孙出身于中产阶级，他父亲常用知足常乐的哲学教育他，要他满足现状。但他还是抵挡不住海外新世界的诱惑，几次三番地出海闯荡，终于有一次被困在... ', '2006-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('强风吹拂', 8615, '\n\n\n三浦紫苑林佩瑾、李建铨、杨正敏', '小说', '广西师范大学出版社', '长跑的目标不是更快，而是更强。\n“明明这么痛苦，这么难过，为什么就是不能放弃跑步？\n因为全身细胞都在蠢蠢欲动，想要感受强风迎面吹拂的滋味。”\n宽政大学宿舍“... ', '2015-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('看不见的城市', 8616, '\n\n\n[意]伊塔洛·卡尔维诺张密', '小说', '译林出版社', '在大陆，王小波、苏童、阿城、止庵是他的忠实粉丝\n在台湾，朱天文，唐诺是卡尔维诺不余遗力的传播者\n在香港，梁文道说他一直在准备谈卡尔维诺，可是一直没准备好\n权... ', '2012-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('夏日终曲', 8617, '\n\n\n[美]安德烈·艾席蒙吴妍蓉', '小说', '外语教学与研究出版社', '2007年出版以来长踞《纽约时报》、美国亚马逊书店、Goodreads各种畅销书榜单。\n根据这部小说改编的电影《请以你的名字呼唤我》荣获2018年第90届奥... ', '2018-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('远山淡影', 8618, '\n\n\n[英]石黑一雄张晓意', '小说', '上海译文出版社', '《远山淡影》是石黑一雄技惊文坛的处女作，一部问世30年仍在不断重印的名著。其“感伤与反讽”的融合、平衡令人犹记。这是一段迷雾重重、亦真亦幻的回忆。战后长崎，... ', '2011-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('银河系边缘的小失常', 8619, '\n\n\n[以色列]埃特加·凯雷特方铁', '小说', '浦睿文化·湖南文艺出版社', '继《突然，响起一阵敲门声》之后，短篇小说大师埃特加·凯雷特创作的全新短篇小说集。\n一个伤感又好笑的马戏团炮弹飞人；一个五十岁还在啃老的窝囊儿子；一个想要重返... ', '2020-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('皮囊', 8620, '\n\n\n蔡崇达', '小说', '天津人民出版社', '一部有着小说阅读质感的散文集，也是一本“认心又认人”的书。\n作者蔡崇达，本着对故乡亲人的情感，用一种客观、细致、冷静的方式，讲述了一系列刻在骨肉间故事。一个... ', '2014-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('了不起的盖茨比', 8621, '\n\n\n[美]斯科特·菲茨杰拉德邓若虚', '小说', '南海出版公司', '★ 20世纪全球百部英语小说第2名\n★ 村上春树最爱的书\n★ 村上春树万字长文导读\n★ 全新译本完美呈现原著魅力\n★ 如果没有与《了不起的盖茨比》相遇，我写... ', '2012-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('球状闪电', 8622, '\n\n\n刘慈欣', '小说', '四川科学技术出版社', '在某个离奇的雨夜，一颗球状闪电闯进了少年的视野。它的啸叫低沉中透着尖利，像是一个鬼魂在太古的荒原上吹着埙。当鬼魂奏完乐曲，球状闪电在一瞬间将少年的父母化为灰... ', '2005-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('红楼梦', 8623, '\n\n\n曹雪芹', '小说', '人民文学出版社', '《红楼梦》，中国古代古代四大名著之一，也是中国古代小说的巅峰之作。全书以荣国府的日常生活为中心，以宝玉、黛玉、宝钗的爱情婚姻悲剧及大观园中点滴琐事为主线，以... ', '2018-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('哈利·波特与魔法石', 8624, '\n\n\n[英]J.K.罗琳苏农', '小说', '人民文学出版社', '一岁的哈利・波特失去父母后，神秘地出现在姨父姨妈家的门前。哈利在姨父家饱受欺凌，度过十年极其痛苦的日子。姨父和姨妈好似凶神恶煞，他们那混世魔王儿子达力――一... ', '2000-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('了不起的盖茨比', 8625, '\n\n\n[美]菲茨杰拉德姚乃强', '小说', '人民文学出版社', '盖茨比为了久久地抱着的一个梦而付出了很高的代价。他死后，尼克发觉是汤姆暗中挑拨威尔逊去杀死盖茨比。他感到东部鬼影幢幢，世态炎凉，便决定回中西部老家去。这是一... ', '2004-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('难哄', 8626, '\n\n\n竹已', '小说', '江苏凤凰文艺出版社', '机缘巧合之下，温以凡跟曾被她拒绝过的高中同学桑延过上了合租的生活。两人井水不犯河水，像是同住一屋檐下的两个陌生人。\n平静的生活中止于某个晚上。温以凡从桑延口... ', '2020-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('故事新编', 8627, '\n\n\n鲁迅', '小说', '人民文学出版社', '《故事新编》是鲁迅的一部短篇小说集，收录了鲁迅在1922年～1935年间创作的短篇小说八篇。1936年1月由上海文化生活出版社初版，列为巴金所编的《文学丛刊... ', '1973-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('海风中失落的血色馈赠', 8628, '\n\n\n[加拿大]阿利斯泰尔·麦克劳德', '小说', '上海文艺出版社', '《海风中失落的血色馈赠》所收入的七个故事，有少年渴望摆脱家族在海岛世代挖煤的命运而在成年之际离家远行，有人到中年的大学教师回忆少年时他那心怀壮志但困居海岛打... ', '2015-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('黄金时代', 8629, '\n\n\n王小波', '小说', '北京十月文艺出版社', '【内容简介】\n写出《黄金时代》之前，我从未觉得自己写得好。——王小波\n本书收入王小波颇负盛名的三篇小说《黄金时代》《三十而立》《似水流年》，其中《黄金时代》... ', '2017-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('德米安', 8630, '\n\n\n[德]赫尔曼·黑塞丁君君、谢莹莹', '小说', '上海人民出版社', '《德米安：埃米尔·辛克莱的彷徨少年时》是黑塞的代表作之一，讲述少年辛克莱寻找通向自身之路的艰辛历程。出生并成长于“光明世界”的辛克莱，偶然发现截然不同的“另... ', '2009-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('挪威的森林', 8631, '\n\n\n[日]村上春树林少华', '小说', '上海译文出版社', '这是一部动人心弦的、平缓舒雅的、略带感伤的恋爱小说。小说主人公渡边以第一人称展开他同两个女孩间的爱情纠葛。渡边的第一个恋人直子原是他高中要好同学木月的女友，... ', '2007-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('冬泳', 8632, '\n\n\n班宇', '小说', '上海三联书店', '班宇（@坦克手贝吉塔）短篇力作首度成集 阿乙•蒋方舟•李诞•谈波 齐声推荐\n《冬泳》收录了班宇的七篇小说。在铁轨、工事与大雪的边缘，游走着一些昔日的身影：印... ', '2018-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('烟与镜', 8633, '\n\n\n[英]尼尔·盖曼王爽', '小说', '江苏凤凰文艺出版社', '编辑推荐\n◆看得头皮发麻的同时，原始的人性一览无遗。\n◆《烟与镜》是格芬奖、斯托克奖、轨迹奖提名、世界奇幻奖提名作品！\n◆作者尼尔·盖曼是当代奇幻大师，被斯... ', '2021-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('伪装学渣', 8634, '\n\n\n木瓜黄', '小说', '北京时代华文书局', '小说讲述了分班后，两位风靡校园的“问题少年”不止分进一个班还成为同桌的故事。他们明明是学霸却要装学渣，浑身都是戏，考试要争倒数第一，表面上天天玩游戏，实际上... ', '2019-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('消失的13级台阶', 8635, '\n\n\n[日]高野和明赵建勋', '小说', '上海文艺出版社', '【内容简介】\n一对老夫妇惨遭杀害。一切证据都指向树原亮，他却因车祸，恰好丧失了案发前后数小时的记忆……\n死刑执行官南乡携手刚假释出狱的纯一调查，希望替这位丧... ', '2020-05-15', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('摆渡人', 8636, '\n\n\n[英]克莱儿·麦克福尔付强', '小说', '百花洲文艺出版社', '单亲女孩迪伦，15岁的世界一片狼藉：与母亲总是无话可说，在学校里经常受到同学的捉弄，唯一谈得来的好友也因为转学离开了。这一切都让迪兰感到无比痛苦。 她决定去... ', '2015-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('追忆似水年华', 8637, '\n\n\n[法]马塞尔·普鲁斯特', '小说', '译林出版社', '普鲁斯特的《追忆似水年华》以回忆的形式对往事作了回顾，有童年的回忆、家庭生活、初恋与失恋、历史事件的观察、以及对艺术的见解和对时空的认识等等。时间是这部小说... ', '2012-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('傲慢与偏见', 8638, '\n\n\n简•奥斯丁(JaneAusten)', '小说', '译林出版社', '本书描写傲慢的单身青年达西与偏见的二小姐伊丽莎白、富裕的单身贵族彬格莱与贤淑的大小姐吉英之间的感情纠葛，充分表达了作者本人的婚姻观，强调经济利益对恋爱和婚姻... ', '2010-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('怪诞故事集', 8639, '\n\n\n[波兰]奥尔加·托卡尔丘克', '小说', '浙江文艺出版社', '◎诺贝尔文学奖得主奥尔加·托卡尔丘克最新小说集\n◎从波兰语直接翻译\n◎2018年诺贝尔文学奖得主（2019年授予）；尼刻奖、布克国际奖等多项大奖获得者\n◎森... ', '2020-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('如父如子', 8640, '\n\n\n[日]是枝裕和、[日]佐野晶丹勇', '小说', '湖南文艺出版社', '当意识到孩 子也在注视着自 己时，那一瞬间，便懂得了什么是如父如子。\n至今为止都过着一帆风顺的人生的野野宮良多，是大型建筑公司里的精英社员。他和妻子绿结婚多... ', '2018-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('情书', 8641, '\n\n\n[日]岩井俊二穆晓芳', '小说', '天津人民出版社', '日本神户，渡边博子在未婚夫藤井树的三周年祭日上又一次陷入到悲痛和思念之中。博子在藤井树的中学同学录里找到了他在小樽市读书时的地址。由于抑制不住对爱人的怀念，... ', '2004-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('海底两万里', 8642, '\n\n\n[法国]儒尔·凡尔纳', '小说', '译林出版社', '本书是法国举世闻名的科幻小说作家儒尔·凡尔纳的代表作之一。\n作者让读者登上“鹦鹉螺号”，以平均十二公里的时速，在将近十个月的海底旅行中，随着尼摩船长和他的“... ', '2002-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('白鹿原', 8643, '\n\n\n陈忠实', '小说', '人民文学出版社', '这是一部渭河平原五十年变迁的雄奇史诗，一轴中国农村班斓多彩、触目惊心的长幅画卷。主人公六娶六丧，神秘的序曲预示着不祥。一个家族两代子孙，为争夺白鹿原的统治代... ', '1997-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('撒哈拉的故事', 8644, '\n\n\n三毛', '小说', '哈尔滨出版社', '三毛作品中最脍炙人口的《撒哈拉的故事》，由12篇精彩动人的散文结合而成，其中《沙漠中的饭店》，是三毛适应荒凉单调的沙漠生活后，重新拾笔的第一篇文字，自此之后... ', '2003-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我们仨', 8645, '\n\n\n杨绛', '小说', '生活·读书·新知三联书店', '《我们仨》是钱钟书夫人杨绛撰写的家庭生活回忆录。1998年，钱钟书逝世，而他和杨绛唯一的女儿钱瑗已于此前（1997年）先他们而去。在人生的伴侣离去四年后，杨... ', '2003-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('目光', 8646, '\n\n\n陶勇12345', '小说', '百花洲文艺出版社', '关于善恶、理想、名利、孤独、生死、自我……《目光》是陶勇医生首部文学随笔，是一个医生的沉思录和成长感悟。\n全书内容以陶勇医生的思想观点脉络划分为关于生死善恶... ', '2020-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('沉默的大多数', 8647, '\n\n\n王小波', '小说', '中国青年出版社', '这本杂文随笔集包括思想文化方面的文章，涉及知识分子的处境及思考，社会道德伦理，文化论争，国学与新儒家，民族主义等问题；包括从日常生活中发掘出来的各种真知灼见... ', '1997-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('看见', 8648, '\n\n\n柴静', '散文', '广西师范大学出版社', '《看见》是知名记者和主持人柴静讲述央视十年历程的自传性作品，既是柴静个人的成长告白书，某种程度上亦可视作中国社会十年变迁的备忘录。十年前她被选择成为国家电视... ', '2013-01-01', -1, 30000, '泰山');
