INSERT INTO `books` VALUES ('当我谈跑步时我谈些什么', 8652, '\n\n\n[日]村上春树施小炜', '散文', '南海出版公司', '他以文字名满全球。\n他的“蓝调”令万千人神迷忧伤。\n文字是他的符号，“跑者蓝调”何尝不是？\n不再是浮华迷茫，不再是旖旎感伤，不再羚羊挂角无迹可寻——写了几十... ', '2009-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('送你一颗子弹', 8653, '\n\n\n刘瑜', '散文', '上海三联书店', '这本书里记录的是作者2005—2009年左右(尤其是2006—2007年)生活里的点点滴滴。在这本书里，被“审视”的东西杂七杂八，有街上的疯老头，有同宿舍的... ', '2010-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('在峡江的转弯处', 8654, '\n\n\n陈行甲', '散文', '人民日报出版社', '《在峡江的转弯处：陈行甲人生笔记》是陈行甲的自传体随笔，从童年岁月写起，写母亲，写爱人；写了从大学毕业到基层工作九年多的生活经历；回顾了作者在巴东任县委书记... ', '2021-01-10', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('人间草木', 8655, '\n\n\n汪曾祺', '散文', '江苏文艺出版社', '《人间草木》(汪曾祺著)是汪曾祺优秀的散文集。当代文坛上,能同时在小说和散文两块田地里经营,且自成一家的并不多,汪曾祺先生算是其中的一个。汪曾祺先生是公认的... ', '2005-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('碎片', 8656, '\n\n\n[意]埃莱娜·费兰特陈英', '散文', '人民文学出版社', '《碎片》（2016）是意大利作家埃莱娜·费兰特20 余年来的书信、访谈和散文集。作家在书中袒露了自己对写作风格和主题的探索历程，并回顾了自己经历的自我怀疑和... ', '2020-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('江城', 8657, '\n\n\n[美]彼得·海斯勒李雪顺', '散文', '上海译文出版社', '1996年8月底一个温热而清朗的夜晚，我从重庆出发，乘慢船，顺江而下来到涪陵。\n涪陵没有铁路，历来是四川省的贫困地区，公路非常糟糕。去哪里你都得坐船，但多半... ', '2012-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('瓦尔登湖', 8658, '\n\n\n[美]梭罗徐迟', '散文', '上海译文出版社', '这本书的思想是崇尚简朴生活，热爱大自然的风光，内容丰厚，意义深远，语言生动，意境深邃，就像是个智慧的老人，闪现哲理灵光，又有高山流水那样的境界。\n书中记录了... ', '2006-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('阅读是一座随身携带的避难所', 8659, '\n\n\n[英]毛姆罗长利', '散文', '北京联合出版公司', '如果世界上有一种叫作阅读家的职业，不会再有人比毛姆更加适合。\n作家热爱读书，这似乎是自然的事，但像毛姆一样，按图索骥，从作家的作品问解到作家的生平和性格，再... ', '2017-06-20', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('沉默的大多数', 8660, '\n\n\n王小波', '散文', '北京十月文艺出版社', '王小波杂文精选集，逝世二十周年精装纪念版！特别收入珍贵手稿！集结精粹杂文，全面呈现一位自由思想者的精神世界！——“从话语中，你很少能学到人性，从沉默中却能。... ', '2017-04-01', -1, 30000, '泰山');
