INSERT INTO `books` VALUES ('撒哈拉的故事', 8662, '\n\n\n三毛', '散文', '北京十月文艺出版社', '★三毛——华文世界里的传奇女子，她的足迹遍及世界各地\n★此篇是三毛最受欢迎的作品，倾倒了全世界的华文读者\n★封面由台湾著名设计师聂永真倾情设计\n★全方位、多... ', '2011-01-07', -1, 30000, '泰山');
