INSERT INTO `books` VALUES ('一只特立独行的猪', 8666, '\n\n\n王小波', '散文', '北京十月文艺出版社', '王小波杂文精选集，逝世二十周年精装纪念版！\n幽默中充满智性，扛起一面自由、独立、理性的精神旗帜！——“我觉得黑色幽默是我的气质，是天生的。”\n“我已经四十岁... ', '2017-04-01', -1, 30000, '泰山');
