INSERT INTO `books` VALUES ('我与地坛', 8741, '\n\n\n史铁生', '散文', '人民文学出版社', '《我与地坛》收入《我与地坛》、《记忆与印象》等以记事为主的散文，配少量图片。《我与地坛》由中国当代著名作家史铁生著。是史铁生文学作品中，充满哲思又极为人性化... ', '2011-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('谈美', 8742, '\n\n\n朱光潜', '散文', '广西师范大学出版社', '本书是朱光潜先生建立其早期美学理论体系的重要著作之一。全书从“谈美”“免俗”，“人心净化”的目标出发，顺着美从哪里来，美是什么及美的特点这一脉络层层展开，娓... ', '2004-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('拍电影时我在想的事', 8743, '\n\n\n[日]是枝裕和褚方叶', '散文', '南海出版公司', '《拍电影时我在想的事》是戛纳电影节金棕榈奖得主是枝裕和历时8年，写下的首部自传性随笔集。\n是枝裕和在书中回顾三十余年的创作生涯，讲述每一部经典作品背后的传奇... ', '2018-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('病隙碎笔', 8744, '\n\n\n史铁生', '散文', '湖南文艺出版社', '病隙碎笔》\n★ 史铁生灵性光辉的生命笔记。读史铁生，不可不读《病隙碎笔》，这是史铁生重要作品之一，是一部充满了深刻生命体验的人生笔记。\n★  受读者喜爱的当... ', '2013-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('郭论', 8745, '\n\n\n郭德纲', '散文', '湖南文艺出版社', '慢火烹茶，解读中国人骨子里的 “忠”与“义”。\n拾遗明清历史、解读市井文化、大话经典名著……郭德纲带你品人情冷暖、世态炎凉；观荣辱纷争、死走逃亡。\n洞察人生... ', '2018-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('36岁，人生半熟', 8746, '\n\n\n宽宽', '散文', '北京联合出版公司', '《36岁，人生半熟》是一个人即将步入中年时的自我反思和发现。27篇随笔篇幅短小而论述入里，有作者寻找自生生命方向之际的刹那体悟，亦是其人生哲学的阶段阐述。质... ', '2020-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('牛津笔记', 8747, '\n\n\n张力奋', '散文', '学林出版社', '本书以日记体的方式结构全篇，从2017年4月17日起，至2017年6月24日，每篇日记配备了一幅作者在牛津拍摄的黑白照片。作者用直白的文字和珍贵的黑白影像记... ', '2020-09-01', -1, 30000, '泰山');
