INSERT INTO `books` VALUES ('走到人生边上', 8749, '\n\n\n杨绛', '散文', '商务印书馆', '人生据说是一部大书。“生、老、病、死”是人生的规律，谁也逃不过。《写在人生边上》是钱钟书先生的第一个集子，由杨绛女士编定。本书则是这个集子的注释，回答了神和... ', '2007-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('把自己当回事儿', 8750, '\n\n\n杨天真', '散文', '北京联合出版公司', '这是一本杨天真的“沟通秘籍”，用zui真诚的方式讲解zui有力的沟通之道。\n真诚、直接、解决问题、\n共情、共识、自我沟通，\n6大核心板块，层层递进，破解沟通... ', '2021-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('爱吃沙拉的狮子', 8751, '\n\n\n[日]村上春树施小炜', '散文', '南海出版公司', '村上春树的日常生活是什么样子？每天在孤独之中默默埋头写作？NO~！！\n他爱爵士乐、威士忌，但同样爱旅行，爱猫咪，爱恶作剧，爱奇思妙想，过着平凡的生活，坐坐公... ', '2015-05-01', -1, 30000, '泰山');
