INSERT INTO `books` VALUES ('过得刚好', 8754, '\n\n\n郭德纲', '散文', '北京联合出版公司', '郭德纲亲笔作品，讲述人生四十年的江湖过往，知无不言，言无不尽。\n迄今为止，唯一人生回顾，荣辱浮沉，冷暖自知，自浊自清自安然。书中的文字记录了郭德纲这些年的心... ', '2013-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('人间好玩', 8755, '\n\n\n蔡澜', '散文', '湖南文艺出版社', '蔡澜为人幽默风雅，以鲜活、生动的文字讲述他的所见所闻，与读者分享他的识见。他说：“为了喜欢写而写，才是一个真正的开端。除了文字之功，还要够真、够坦白。”一篇... ', '2021-04-01', -1, 30000, '泰山');
