INSERT INTO `books` VALUES ('文化苦旅', 8757, '\n\n\n余秋雨', '散文', '东方出版中心', '《文化苦旅》是余秋雨的第一部散文合集，所收作品主要包括两部分，一部分是历史、文化散文，散点论述，探寻文化；另一部分是回忆散文。 ', '2001-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('阅读是一座随身携带的避难所', 8758, '\n\n\n[英]威廉·萨默塞特·毛姆夏高娃', '散文', '江西人民出版社', '◆用一间书房抵抗这个不太完美的世界，用一本书来指导我们的阅读趣味。\n天才小说家毛姆的阅读指南，教你阅读的方法、技巧，讲述大师和巨匠的秘密。\n◆关于如何阅读，... ', '2020-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('云雀叫了一整天', 8759, '\n\n\n木心', '散文', '广西师范大学出版社', '在法兰克福，木心写道，飞越大西洋，伦敦消失，法兰克福出现，机组人员没发任何表格，心里有点痒兮兮。登录一个不设防的国家……画廊主、餐馆老板、咖啡厅领班都一口流... ', '2009-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('亲爱的三毛', 8760, '\n\n\n三毛', '散文', '北京十月文艺出版社', '她以一支笔坚持看守个人文字上的简单和朴素\n从遥远的撒哈拉到敦煌戈壁，她不随波逐流，也不诠释人生，只做生活的见证者\n她是我们心中浪漫、洒脱、真性情的永远的三毛... ', '2017-03-01', -1, 30000, '泰山');
