INSERT INTO `books` VALUES ('六里庄遗事', 8762, '\n\n\n东东枪', '散文', '上海三联书店', '六里庄，是唐时国都长安以东六里许的普通村庄。《六里庄遗事》凡十二卷，包括近600则片断文字，大多是对六里庄内人物及相关人等事迹的钩沉杂忆。本书是作者东东枪酝... ', '2019-02-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('一色一生', 8763, '\n\n\n[日]志村福美米悄', '散文', '上海人民出版社·光启书局', '◆日本染织界“人间国宝”自传性散文；\n◆ 大佛次郎奖获奖作品；长销日本三十年的经典之作，首次国际授权\n◆日本政府授予她文化勋章（国家最高荣誉）和文化功劳者称... ', '2021-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('贾想II', 8764, '\n\n\n贾樟柯、万佳欢编', '散文', '理想国|台海出版社', '◎内容简介\n本书收录贾樟柯在2008到2016年间的文章、演讲与对谈，这些与电影作品并行的文字，记录下他电影内外的生活和思索，对于历史和社会剧变的关怀和反思... ', '2018-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('沿着塞纳河到翡冷翠', 8765, '\n\n\n黄永玉', '散文', '作家出版社', '一位67位岁的中国画家，支起画架，安顿好三脚凳，安然从在巴黎塞纳河畔、翡冷翠（佛罗伦萨）优雅的街头，专心画他的画。这是1991年的春天夏天的事情，画家黄永玉... ', '2006-11-01', -1, 30000, '泰山');
