INSERT INTO `books` VALUES ('观念的水位', 8767, '\n\n\n刘瑜', '散文', '浙江大学出版社', '作为国内公共领域最重要的声音之一，刘瑜的文字向来拥趸众多，之前的作品更多关注美国民主，与中国相关度不高。而本书更多关注东亚、中东欧、南美洲以及非洲国家的政治... ', '2013-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('造一所不抗拒生活的房子', 8768, '\n\n\n赵扬', '散文', '北京联合出版公司', '本书记录了赵扬十余年的建筑实践及求学生涯，可以看到一位青年建筑的成长历程，在不断的自我否定又重建的过程中逐步形成自己的“建筑观”，认为建筑师不应该固守理念，... ', '2021-01-15', -1, 30000, '泰山');
