INSERT INTO `books` VALUES ('回答不了', 8770, '\n\n\n匡扶', '散文', '浙江文艺出版社', '每个深夜，睡前你想到的最后一个念头是什么？\n在漫长的日复一日里，你是否觉得生活挺没劲的，但在某个瞬间又生机勃勃、斗志昂扬起来？\n爱情、工作和生活日常，是在哪... ', '2018-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('一只特立独行的猪', 8771, '\n\n\n王小波', '散文', '北方文艺出版社', '这本书里除了文化杂文，还有给其他书写的序言与跋语。这些序言与跋语也表明了我的一些态度。除此之外，还有一些轻松的随笔。不管什么书，我都不希望它太严肃，这一本也... ', '2006-04-01', -1, 30000, '泰山');
