INSERT INTO `books` VALUES ('阿弥陀佛么么哒', 8773, '\n\n\n大冰', '散文', '湖南文艺出版社', '《阿弥陀佛么么哒》一书记录了大冰十余年的江湖游历，以及他和他朋友们的爱与温暖的传奇故事。\n这些故事与风花雪月无关，与鸡汤小清新无关，有的是无畏的奋斗和孤身的... ', '2015-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('有所不为的反叛者', 8774, '\n\n\n罗新', '散文', '上海三联书店', '罗新教授近年撰写了许多“与专业反思有关”的学术随笔，反映他对历史、历史学和历史学工作的反思与理解。这些与他走出书斋用自己的脚步丈量历史、发现中国、认识世界异... ', '2019-05-01', -1, 30000, '泰山');
