INSERT INTO `books` VALUES ('悲喜自渡', 8777, '\n\n\n季羡林', '散文', '江苏凤凰文艺出版社', '这本散文集精心收录国学大师季羡林创作的三十七篇经典佳作，记述了他对人生、自然、故乡、孤独的思考和感悟。\n从《夹竹桃》《石榴花》的草木情怀，到《月是故乡明》的... ', '2019-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('容忍与自由', 8778, '\n\n\n胡适', '散文', '云南人民出版社', '《容忍与自由》是一部杂文选集，收录了18篇胡适先生的经典杂文，行文流畅，说理深致，对于我们今天的生活仍有启发。 ', '2015-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我执', 8779, '\n\n\n梁文道', '散文', '广西师范大学出版社', '本书为梁文道先生所撰写的散文随笔集，是以香港《成报》文采版专栏“秘学笔记”的文字为主，谈及爱情婚姻、日常生活、疾病经历、信仰感悟、城市文化、文学艺术、历史记... ', '2010-10-01', -1, 30000, '泰山');
