INSERT INTO `books` VALUES ('笑场', 8781, '\n\n\n李诞', '散文', '北京联合出版公司', '“师傅，你法名为什么叫空舟？”\n“大方丈说我度不了人，也难自度，所以赐名空舟，由我自横。”\n“那我还跟着你干吗……”\n“你执念太重，跟着谁也到不了彼岸，不如... ', '2020-04-01', -1, 30000, '泰山');
