INSERT INTO `books` VALUES ('我不', 8788, '\n\n\n大冰', '散文', '湖南文艺出版社', '善意能消戾，善意能得缘，善意能带业往生，善意能回头是岸。\n善意能够帮人捕捉并建立起独特的幸福感。\n“我不”是一种善意坦然，也是一种善意的随缘，更是一句善意的... ', '2017-08-01', -1, 30000, '泰山');
