INSERT INTO `books` VALUES ('常识与通识', 8797, '\n\n\n阿城', '散文', '上海三联书店', '《常识与通识》里的十二篇文章，为阿城先生为《收获》所写的专栏，原题为“煞风景”，因为讲常识，常常煞风景。这些文章写自洛杉矶、墨西哥、上海、台北……游历世界的... ', '2019-04-01', -1, 30000, '泰山');
