INSERT INTO `books` VALUES ('又是愉快的一天', 8809, '\n\n\n陶立夏', '散文', '湖南文艺出版社', '畅销书作家、译者、摄影师陶立夏全新生活图文集。\n在足不出户的一年里，曾经不断远行的陶立夏重新回观自我，把生活、物品、情感的各个角度展示给读者，并为大家阐述很... ', '2020-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('那些忧伤的年轻人', 8810, '\n\n\n许知远', '散文', '海南出版社', '作者生于70年代后期，有人说这个年代出身的是充满物质感的一代，显然这是个武断的结论，每一代人中都会有人积极回应智力的承传，特别是生命力与想像力充沛的青年。《... ', '2001-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('惶然录', 8811, '\n\n\n[葡]费尔南多·佩索阿韩少功', '散文', '上海文艺出版社', '★  文字典雅、精准、洁净，充满了诗性之美和智性之魅，体现其对思想对生活一种智慧的悟力，穿透了尘世和自然。\n★  这是一本意蕴深厚的书，只要你不断阅读，你就... ', '2019-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('无所畏', 8812, '\n\n\n冯唐', '散文', '北京联合出版公司', '冯唐全新作品，一部坦露自我的真诚之作。\n全书分为如何获得成功、爱情如何对抗时间、生活怎么过才算有意义、自我价值如何体现四个部分，并收录《如何避免成为一个油腻... ', '2018-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('云雀叫了一整天', 8813, '\n\n\n木心', '散文', '广西师范大学出版社', '★ 木心先生诗歌代表作：在欧盟各国转悠，飞越大西洋，伦敦消失，法兰克福出现，在空荡的机场大厅、在清洁石子路边摆开的桌椅，在四门敞开的歌剧院，在欧罗巴的景内景... ', '2013-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('康巴情书', 8814, '\n\n\n杜冬', '散文', '凤凰出版社', '康巴，晴空。\n那里有个雪山一样纯净的康巴姑娘。\n理塘，世界上离天空最近的康巴小城，天空纯净一如史前，神灵和敬畏无处不在，恣意与炽热的眼神在晴空下闪烁，阳光和... ', '2013-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('野火集', 8815, '\n\n\n龙应台', '散文', '文汇出版社', '龙应台常常针对一种社会现象，一类具体事物，甚至于一个人、一句话、一件事，给予无情的透视和直接的批评，马上让人心有戚戚焉。这些事，就发生在周围，看得见，摸得着... ', '2005-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('遍地风流', 8816, '\n\n\n阿城', '散文', '江苏凤凰文艺出版社', '《遍地风流》是阿城先生公认的经典之作，屡次再版，好评不衰，奠定了其汉语写作界塔尖的地位。\n此次出版的是阿城先生亲自编订的全新版本，收录了70篇作者的短篇小说... ', '2016-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('读库2101', 8817, '\n\n\n张立宪主编', '散文', '新星出版社', '1999年，在外打工七年的田维堂回到贵州老家结婚生子，当农民种地的日子过得依然艰难。一年多后尝试做生意、当学徒，并不顺利，2003年初，怀揣着“赚够一万块钱... ', '2021-02-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('千年一叹', 8818, '\n\n\n余秋雨', '散文', '长江文艺出版社', '《千年一叹》记录了余秋雨先生在2000年穿越数万公里考察伊斯兰文明遗迹的经历。行迹遍布希腊、埃及、以色列、巴勒斯坦、约旦、伊拉克、伊朗、巴基斯坦、尼泊尔等。... ', '2012-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('空谷幽兰', 8819, '\n\n\n[美]比尔·波特明洁', '散文', '当代中国出版社', '《空谷幽兰》是美国汉学家比尔·波特写的一部关于中国的“寻隐之旅”。他通过20世纪八九十年代亲身探访隐居在终南山等地的中国现代隐士，引出了中国隐逸文化及其传统... ', '2006-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('今天也要用心过生活', 8820, '\n\n\n松浦弥太郎张富玲', '散文', '湖南人民出版社', '《今天也要用心过生活》是松浦弥太郎广为人知的一部作品，在这本书中，松浦弥太郎分享了56篇生活巧思，讲述如何与生活中的大事小事面对面，如何让每一天都丰富而充实... ', '2020-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我所理解的生活', 8821, '\n\n\n韩寒', '散文', '浙江文艺出版社', '★韩寒新作《我所理解的生活》，三十岁的韩寒，第一次自我剖析。\n★韩寒所有作品中，删减幅度最小！收录韩三篇：谈革命，说民主，要自由。\n★少说，多做，不解释。\n... ', '2013-01-08', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('五味', 8822, '\n\n\n汪曾祺', '散文', '山东画报出版社', '汪曾祺既不是达官贵人，又不是商贾巨富，因此所食、所喜的多是地方风味和民间小食，他谈萝卜、豆腐，讲韭菜花、手把肉，皆是娓娓道来，从容闲适；读的人则津津有味，满... ', '2005-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('坦白书', 8823, '\n\n\n马良', '散文', '湖南文艺出版社', '本书是艺术家马良对于童年的回忆，对于理想的探索，承载了马良的执着追寻。\n在书中，马良不但记录了对童年的回忆，也分享了对摄影、绘画等艺术创作的感悟，对理想、对... ', '2012-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('那些安静的日子', 8824, '\n\n\n林采宜', '散文', '文化发展出版社', '本书著名经济学家，人文学者林采宜的随笔、小说集，收录了作者近两年的随笔和短篇小说。其中随笔主要从经济学、哲学、心理学及社会伦理等多种角度解读、点评社会生活的... ', '2021-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('反正竞赛还很长', 8825, '\n\n\n[韩]罗英石ATELOO', '散文', '理想国丨广西师范大学出版社', '正当事业巅峰之际，罗英石把自己送到最遥远的地方：冰岛，展开了一趟事前毫无计划的极光探寻之旅。\n本书中，身为金牌制作人的罗PD全面回顾了韩国国民综艺《两天一夜... ', '2016-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('永恒的时光之旅', 8826, '\n\n\n[日]星野道夫游韵馨', '散文', '广西师范大学出版社', '《永恒的时光之旅》收录星野道夫未公开摄影作品及西伯利亚绝笔日记 ，是对星野道夫史诗般摄影生涯的回顾和致敬。星野道夫用镜头扑捉自然的至美和生命的脆弱与坚强，用... ', '2016-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('不安之书', 8827, '\n\n\n费尔南多·佩索阿刘勇军', '散文', '中国文联出版社', '《不安之书》是费尔南多•佩索阿的代表作之一，也是他在华语市场上失落已久的经典之作。它是曾经长期散佚的作品，多为“仿日记”片断体，由众多研究专家搜集整理而成。... ', '2014-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('每天都是小春日和', 8828, '\n\n\n[日]津端英子、[日]津端修', '散文', '新星出版社', '名古屋市郊的一片杂木林和一居小木屋，是津端夫妇的梦幻田园。\n为圆妻子的田园梦，酷爱帆船和航海的修一停泊在这块乡间土地，亲手搭起木屋，开辟菜园，从此与太太英子... ', '2017-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('玉山丹池', 8829, '\n\n\n(美)何瞻', '散文', '上海人民出版社', '石听泉、卜正民、王立群、梅新林、徐永明等海内外学者联袂推荐\n英语世界首部综述中国传统游记文学的专著\n展现古代文人的自然、地理和社会空间观念\n探寻寄情山水时亦... ', '2021-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('稻草人手记', 8830, '\n\n\n三毛', '散文', '北京十月文艺出版社', '本卷收录散文16篇，其中大部分文章记述了三毛在加纳利群岛上的生活。\n《稻草人手记》笔调幽默，点点滴滴尽述生活情趣，一个率性女子不加矫饰的灿然风貌跃然纸上，令... ', '2009-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('陶庵梦忆 西湖梦寻', 8831, '\n\n\n[明]张岱夏咸淳、程维荣校注', '散文', '上海古籍出版社', '《陶庵梦忆》展示了明末色彩斑斓的市民阶层的生活情态，书中所载多琐屑之事，涉及城市胜概、山川景物、风俗人情、文学艺术等各个方面。 ', '2001-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('梦里花落知多少', 8832, '\n\n\n三毛', '散文', '北京十月文艺出版社', '书中先是记录了三毛与荷西在撒哈拉沙漠结婚，白手成家的生活，而此时她的文学创作生涯也随之开启；后来移居加纳利群岛后，三毛的生活渐趋安定，她的创作也达到了很高的... ', '2011-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('岸萤', 8833, '\n\n\n陈传兴', '散文', '中信出版集团', '作为上世纪70年代留学法国的文化先行者，陈传兴亲历了巴黎最后的黄金时代，在时代的尾声中收集并纪录下哲学、艺术、影像的种种刻痕，并在之后多年深耕于影像的探索和... ', '2020-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('不必交谈的时刻', 8834, '\n\n\n樊小纯', '散文', '重庆出版社', '让时间时间，给语言语言\n樊小纯 双重独白 纽约西21街笔记\n随书附赠作者手写卡片\n.\n《不必交谈的时刻》是一本特别的随笔集，它并不时髦，但它庞杂、耐读，是樊... ', '2016-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('扔掉书本上街去', 8835, '\n\n\n[日]寺山修司高培明', '日本文学', '新星出版社', '寺山修司是一个跨界奇人，既是优秀的电影导演、演员，也是杰出的诗人、评论家和戏剧家。他执导的《再见箱舟》《死者田园祭》被列为电影旬报年度十佳影片，并获戛纳电影... ', '2017-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('寂静的深度', 8836, '\n\n\n[美]马克·斯特兰德光哲', '散文', '全本书店|民主与建设出版社', '美国普利策诗歌奖得主、桂冠诗人马克.斯特兰德在大画家霍珀那里看到一个他似曾相识的世界：“ 常常感到霍珀绘画中的那些场景，是我自己过去经历过的”，总是被“抛置... ', '2018-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('步履不停', 8837, '\n\n\n[日]是枝裕和郑有杰', '日本文学', '北京联合出版公司', '★电影大师是枝裕和亲自撰写，经典代表作原著小说\n★拿遍各大国际影展重要奖项，全球影迷热泪推荐！\n★绝无冷场的故事，比电影更幽微的人性暗涌；一茶一饭的淡香，触... ', '2017-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('罗生门', 8838, '\n\n\n[日]芥川龙之介林少华', '日本文学', '上海译文出版社', '芥川龙之介(1892—1927)，日本新思潮派代表作家，创作上既有浪漫主义特点，又具有现实主义倾向。以其名字命名的“芥川奖”成为日本文坛的重要奖项之一。作品... ', '2008-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('心', 8839, '\n\n\n[日]夏目漱石黄悦生', '日本文学', '江苏凤凰文艺出版社', '日本国民级大作家夏目漱石代表作，日版销量过700万册，在日本多年蝉联畅销榜首！\n=========================\n先生自杀了。留下一封长长... ', '2021-03-30', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('山月记', 8840, '\n\n\n[日]中岛敦韩冰、孙志勇', '散文', '中华书局', '这是日本天才作家中岛敦的第一本中文简体版精选集，收录中岛敦《山月记》《弟子》《李陵》《光•风•梦》等九篇代表作。中岛敦的小说多取材于中国古典故事，独出心裁，... ', '2013-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('人间值得', 8841, '\n\n\n[日]中村恒子、[日]奥田弘美', '日本文学', '北京日报出版社', '未来岁月漫长，依旧值得期待。\n人如何度过一生，才不会辜负生命？面对人生的终极问题，当了70年心理医生的作者中村恒子，将其90年的人生智慧，化为本书六大篇章关... ', '2019-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('金阁寺', 8842, '\n\n\n[日]三岛由纪夫唐月梅', '日本文学', '上海译文出版社', '三岛由纪夫重要代表作，发表于1956年，长篇小说。\n《金阁寺》取材于1950年金阁寺僧徒林养贤放火烧掉金阁寺的真实事件。据林养贤说他的犯罪动机是对金阁寺的美... ', '2009-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('心', 8843, '\n\n\n夏目漱石林少华', '日本文学', '青岛出版社', '《哥儿》通过一个不谙世故、坦率正直的鲁莽哥儿踏入社会后同周围俗物展开的种种戏剧性冲突，辛辣而巧妙地讽刺了社会上的丑恶现象，鞭挞了卑鄙、权术和虚伪，赞美了正义... ', '2005-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('ZOO', 8844, '\n\n\n[日]乙一李颖秋', '日本文学', '当代世界出版社', '每天早上都有一张我女朋友尸体的照片放到我的收件箱里，照片显示着女朋友的尸体正在一天天地生蛆、腐化。她是在我们去过动物园之后失踪的。所有人都认为她只是失踪了，... ', '2007-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('告白', 8845, '\n\n\n[日]凑佳苗竺家荣', '日本文学', '湖南文艺出版社', '当伦理和正义出现分歧，你选择站在哪边？\n“爱美的死并非意外，而是被我们班的学生杀害的。”故事以爱女死于校内的中学女教师在班会上的告白开始。这个单亲妈妈独自抚... ', '2016-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我是猫', 8846, '\n\n\n[日]夏目漱石刘振瀛', '日本文学', '上海译文出版社', '夏目漱石（1867-1916），日本近代文学作家的代表，竖起批判现实主义文学的丰碑，被誉为“国民作家”。《我是猫》是他的成名作，同时也是其代表作之一。\n主人... ', '2007-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('挪威的森林', 8847, '\n\n\n[日]村上春树林少华', '日本文学', '上海译文出版社', '《挪威的森林》是日本作家村上春树所著的一部长篇爱情小说，影响了几代读者的青春名作。故事讲述主角渡边纠缠在情绪不稳定且患有精神疾病的直子和开朗活泼的小林绿子之... ', '2018-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('白夜行', 8848, '\n\n\n[日]东野圭吾刘姿君', '日本文学', '南海出版公司', '“只希望能手牵手在太阳下散步”，这个象征故事内核的绝望念想，有如一个美丽的幌子，随着无数凌乱、压抑、悲凉的故事片段像纪录片一样一一还原：没有痴痴相思，没有海... ', '2008-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('坡道上的家', 8849, '\n\n\n[日]角田光代杨明绮', '日本文学', '浙江人民出版社', '新手妈妈里沙子，被选为了一名陪审员，接受审判的是一名杀害幼女的“恶母”。随着庭审的深入，里沙子却发现被告和自己是如此相似……\n为了育儿放弃职业生涯，却因此失... ', '2020-01-01', -1, 30000, '泰山');
