INSERT INTO `books` VALUES ('源氏物语', 8854, '\n\n\n[日]紫式部丰子恺', '日本文学', '人民文学出版社', '《源氏物语》以日本平安王朝全盛时期为背景，通过主人公源氏的生活经历和爱情故事，描写了当时社会的腐败政治、淫乱生活、上层贵族之间的互相倾轧和权力斗争，而源氏的... ', '2015-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('春雪', 8855, '\n\n\n（日）三岛由纪夫唐月梅', '日本文学', '上海译文出版社', '《春雪》是三岛文学艺术美的升华。川端康成把《春雪》誉为现代的《源氏物语》，是作者“绚丽才华的升华”。三岛本人曾总结说：“《春雪》是王朝式的恋爱小说，即写所谓... ', '2010-08-01', -1, 30000, '泰山');
