INSERT INTO `books` VALUES ('挚友', 8861, '\n\n\n[日]川端康成', '日本文学', '杨伟', '★诺贝尔文学奖得主、日本殿堂级唯美文学大师川端康成，不被遗忘的传世之作，中文简体版首次出版！\n川端康成极为欣赏纤细的美，喜爱用那种笔端常带悲哀，兼具象征性的... ', '2018-04-01', -1, 30000, '泰山');
