INSERT INTO `books` VALUES ('新参者', 8868, '\n\n\n[日]东野圭吾岳远坤', '日本文学', '南海出版公司', '【新参者】新加入、刚到来的人。\n东京，日本桥。一个单身女人在公寓内被勒死。警方发现她最近刚搬到这里居住。\n负责调查的是也刚调到日本桥地区的刑警加贺。仙贝店、... ', '2010-05-01', -1, 30000, '泰山');
