INSERT INTO `books` VALUES ('黑箱', 8877, '\n\n\n[日]伊藤诗织匡匡', '日本文学', '中信出版集团', '【内容简介】\n性侵的案发现场，隔绝的私密空间，被称为“黑箱”，而揭开这个“黑箱”时，暴露出来的则是调查机构与司法体系中的更为巨大的“黑箱”。\n本书是日本#M... ', '2013-10-01', -1, 30000, '泰山');
