INSERT INTO `books` VALUES ('金阁寺', 8879, '\n\n\n[日]三岛由纪夫曹曼', '日本文学', '天津人民出版社', '蓬勃而颓唐，自负而不安，因美而毁灭，谁能凭爱意将金阁占有？\n“世界上没有比金阁更美的东西”，美是怨敌，语言大师三岛由纪夫根据真实事件创作而成，呈现一个自卑的... ', '2010-09-01', -1, 30000, '泰山');
