INSERT INTO `books` VALUES ('小偷家族', 8894, '\n\n\n[日]是枝裕和赵仲明', '日本文学', '北京联合出版公司', '★ 戛纳电影节金棕榈奖原著小说\n年度现象级文艺片，国内院线日本真人电影票房冠军\n豆瓣评分8.8，18万影迷热泪推荐！\n第71届戛纳电影节 (2018) 主竞... ', '2015-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('时生', 8895, '\n\n\n东野圭吾徐建雄', '日本文学', '南海出版公司', '读了《解忧杂货店》意犹未尽的话，来读《时生》吧，会给你更多温暖与感动\n东野圭吾暖心力作  给现代人久违的触动：你从没觉得能来到这世上真好吗？\n日本万名东野粉... ', '2017-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('细雪', 8896, '\n\n\n[日]谷崎润一郎周逸之', '日本文学', '译林出版社', '《细雪》的舞台设在上世纪三四十年代之交的大阪，昔日望族莳冈家有漂亮的四姐妹：大姐鹤子守旧、二姐幸子圆融、三妹雪子内向、小妹妙子奔放。故事以大龄未婚的雪子一次... ', '2018-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('假面山庄', 8897, '\n\n\n[日]东野圭吾陈文娟', '日本文学', '北京十月文艺出版社', '◆东野圭吾：《假面山庄》是我的自信之作。我在构思时萌生出一个很妙的主意，于是写作一气呵成。\n◆这不仅是一本推理小说。读完全书，才能领悟深埋心底的巨大的爱。\n... ', '2015-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('祈祷落幕时', 8898, '\n\n\n(日)东野圭吾代珂', '日本文学', '南海出版公司', '★ 渴望幸福的祈祷一重又一重，终有落幕之时。\n★ 东野圭吾感人至深的亲情力作\n★ 获2014年吉川英治文学奖，上市第1周即登上纪伊国屋书店畅销榜第1名\n★ ... ', '2009-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('幻夜', 8899, '\n\n\n（日）东野圭吾李炜', '日本文学', '南海出版公司', '《白夜行》姊妹篇   2004年入围第131届直木奖\n是什么样的过去，造就了她的魔性？\n是什么样的幻影，操纵着他的灵魂？\n我们只能走在幻夜的路上，即使四周明... ', '2021-02-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('寺山修司幸福论', 8900, '\n\n\n[日]寺山修司杨玲', '日本文学', '浦睿文化·湖南文艺出版社', '★编辑推荐★\n这是寺山修司关于幸福的一次次自我剖析。\n他曾呼喊，“我是不幸的，请爱这个不幸的我吧”！\n他用尽幻想与幽默来表演、释放，把自己从被定义中解放出来... ', '2002-02-01', -1, 30000, '泰山');
