INSERT INTO `books` VALUES ('在黑暗中等', 8978, '\n\n\n[日]乙一田秀娟', '日本文学', '南海出版公司', '★16岁轰动日本文坛的天才作家，乙一长篇小说代表作\n★在名为“世界”的这锅浓汤中，人这么多，我们还是孤单。但也许我们终将会遇到一个人，理解或接纳我们吧。\n★... ', '2017-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('活了100万次的猫', 8979, '\n\n\n佐野洋子唐亚明', '日本文学', '接力出版社', '有一只100万年也不死的猫，其实它死了100万次，又活了100万次。是一只漂亮的虎皮花猫。有100万个人宠爱过它，有100万个人在它死的时候哭过，可是它连一... ', '2011-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('草枕', 8980, '\n\n\n[日]夏目漱石陈德文', '日本文学', '上海译文出版社', '《草枕》是夏目漱石的前期的重要代表作之一。小说写\"我\"--一个青年画家为了躲避俗世的忧烦，寻求\"非人情\"的美的世界，来到了一个偏远的山村，以及在那里的所见所... ', '2012-01-01', -1, 30000, '泰山');
