INSERT INTO `books` VALUES ('哭泣的骆驼', 8998, '\n\n\n三毛', '随笔', '哈尔滨出版社', '在《哭泣的骆驼》中，三毛依然恋恋着墨沙漠生活周遭的人与事，《收魂记》、《搭车客》、《逍遥七岛游》、《一个陌生人的死》、《大胡子与我》等篇，情趣盎然；《沙巴军... ', '2020-08-01', -1, 30000, '泰山');
