INSERT INTO `books` VALUES ('心安即是归处', 9001, '\n\n\n季羡林', '随笔', '古吴轩出版社', '季羡林经历过人生的大苦大悲，生命的跌宕起伏。然而先生的一生，不争不辩，不怨不艾，满怀天真，执着自己的执着，安于当下。是世界上仅有的精通于吐火罗文的几位学者之... ', '2012-07-01', -1, 30000, '泰山');
