INSERT INTO `books` VALUES ('一个人就一个人', 9003, '\n\n\n刘同', '随笔', '北京联合出版公司·磨铁图书', '一个人，不是数字上的孤立，而是人格上的独立。\n在别人的故事里，你永远成不了主角。\n有底气一个人面对全世界，才有资格与世界平等对谈。\n一个人是所有生活，也是全... ', '2020-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('受戒', 9004, '\n\n\n汪曾祺', '随笔', '北京十月文艺出版社', '《汪曾祺集》是一部中国当代文学大家汪曾祺作品的精美选本。《汪曾祺集》由北大学者倾力锤炼、打造，选目精当，选本权威。文集共分四本，《邂逅》、《受戒》为汪氏小说... ', '1987-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('坟', 9005, '\n\n\n鲁迅', '随笔', '人民文学出版社', '《坟》收作者1907年至1925年所作论文二十三篇。1927年3月由北京未名社初版，1929年3月第二次印刷时曾经作者校订。1930年4月第三次印刷改由上海... ', '2017-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('她们', 9006, '\n\n\n阎连科', '随笔', '河南文艺出版社', '★阎连科全新长篇散文，一部深刻书写女性命运的圆梦之作！\n阎连科等待十年之后的圆梦之作，这一次他终于可以提起笔，将文字投向世代更迭中女人的命运。他说：“这部散... ', '2008-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('古文观止', 9007, '\n\n\n吴楚材、吴调侯', '随笔', '中华书局', '《古文观止》是清康熙年间吴乘权、吴大职编选的一部古文读本，凡十二卷，收录自先秦至明末的散文二百二十二篇，每篇都有注释和评论。据《左传》襄公二十九年记载，吴公... ', '2018-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('爱你就像爱生命', 9008, '\n\n\n王小波新经典文化', '随笔', '北京十月文艺出版社', '★王小波唯一一本爱情专著。\n★征集网友十个最想问的关于爱情与人生的问题，李银河给出睿智答复！\n★王小波全部作品的独家授权，最全面、最值得收藏的版本！\n★李银... ', '2018-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我与地坛', 9009, '\n\n\n史铁生', '随笔', '人民文学出版社', '《我与地坛(纪念版)》是史铁生文学作品中，充满哲思又极为人性化的代表作之一。其前两段被纳入人民教育出版社的高一教材中。前两部分注重讲地坛和他与母亲的后悔，对... ', '2018-11-20', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我的阿勒泰', 9010, '\n\n\n李娟', '随笔', '长江文艺出版社', '李娟的散文成名作和代表作之一。原生态记录了作者在疆北阿勒泰地区生活的点点滴滴，包括人与事的记忆和感悟。全书文字明净，质地纯粹，原生态地再现了疆北风物人情，充... ', '2018-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('冬牧场', 9011, '\n\n\n李娟', '随笔', '新星出版社', '我从不掩饰自己对《冬牧场》的偏爱，它应该是目前为止自己最重要的一本书吧。——李娟\n李娟非虚构长篇《冬牧场》首推精装珍藏版，精细校订，全新自序，新增26张冬牧... ', '2018-05-25', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('冷场', 9012, '\n\n\n李诞', '随笔', '四川文艺出版社', '《脱口秀大会》主持人、网络人气吐槽大V李诞诚意新作，人间百态故事集。\n韩寒监制，「ONE 一个」工作室出品。\n一个平行宇宙中的李诞，嬉笑怒骂背后对人间的终极... ', '2012-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('天长地久', 9013, '\n\n\n龙应台', '随笔', '湖南文艺出版社', '美君来自浙江。她二十岁爱上的男子，来自湖南。\n他们走过的路，是万里江山，满目烟尘；\n怀着“温情与敬意”，我感恩他们的江山、他们的烟尘，\n给了我天大地大、气象... ', '2003-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('夏日走过山间', 9014, '\n\n\n[美]约翰·缪尔刘颖', '随笔', '天津人民出版社', '1869年，缪尔受朋友邀请前往约塞米蒂山谷的内华达山脉牧羊和考察，沿途写下《夏日走过山间》。在山间行走的3个月使他认识到自然放牧对山地草原的破坏是无法逆转的... ', '2018-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('流言', 9015, '\n\n\n张爱玲', '随笔', '北京十月文艺出版社', '读《小团圆》，不能不看张爱玲散文\n全新修订，华丽上市！\n散佚作品《不得不说的废话》等七篇首次收录，最新增补《炎樱衣谱》《寄读者》两篇文章。\n读《小团圆》，对... ', '2020-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('湘行散记', 9016, '\n\n\n沈从文、黄永玉（图）', '随笔', '北岳文艺出版社', '这些信件及信中所附插图，作者生前未公开发表。1991年由沈虎雏整理、编辑成《湘行书简》，其中“引子”三函为张兆和致沈从文，“尾声”一函为沈从文致沈云六，余为... ', '2000-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我与地坛', 9017, '\n\n\n史铁生', '随笔', '人民文学出版社', '《我与地坛》是史铁生的经典散文集，2010年12月31日，史铁生离开，这本书问世。此后七年，这本书以每年近30万册的数量持续畅销。千千万万读者从《我与地坛》... ', '2008-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('家人闲坐，灯火可亲', 9018, '\n\n\n汪曾祺', '随笔', '古吴轩出版社', '家贵在传承，父辈身上的特质影响他一生：汪曾祺出生在一个地主家庭，祖父白手起家，有两千多亩地，两家药铺，却生活节俭，一个咸鸭蛋要分两顿吃，爱花大价钱收藏字画。... ', '2001-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('写在人生边上', 9019, '\n\n\n钱钟书辽宁人民出版社', '随笔', '辽海出版社', '人生据说是一部大书。 \n假使人生真是这样，那么，我们一大半作者只能算是书评家，具有书评家的本领，无须看得几页书，议论早已发了一大堆，书评一篇写完交卷。 \n但... ', '2002-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('先知', 9020, '\n\n\n(黎巴嫩)纪伯伦冰心', '随笔', '译林出版社', '纪伯伦是著名的黎巴嫩作家，自幼受阿拉伯文化和西方文化的熏陶，并从中汲取营养。他着眼东方，也注视西方，希冀通过文学创作来唤醒东方，改造东方；警策西方，改造西方... ', '2019-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('行者无疆', 9021, '\n\n\n余秋雨', '随笔', '华艺出版社', '2000年8月到2001年1月的这6个月，余秋雨走过了欧洲26个国家96个城市。《行者无疆》记录了作者在旅程中的全部感受，为一部考察西方文明的随笔集。\n余秋... ', '2021-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('山居笔记', 9022, '\n\n\n余秋雨', '随笔', '文汇出版社', '《山居笔记》是余秋雨在1992年至1994年间，全身心地用两年多的时间创作成的，共十一篇文章。写作期间，作者辞去了学院的职务，不再上班；同时写作又与考察联在... ', '2017-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我与父辈', 9023, '\n\n\n阎连科', '随笔', '河南文艺出版社', '◆荒诞现实主义大师剖析自我的真情之作：在我所有作品中，这是一颗钻石，和书的厚重相比，奖项和盛誉都太轻了。\n◆独家收录阎连科真挚长文《被我走丢了的家》，理解父... ', '2016-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('新冠时代的我们', 9024, '\n\n\n[意]保罗·乔尔达诺魏怡', '随笔', '上海译文出版社', '《质数的孤独》作者、粒子物理学博士保罗•乔尔达诺的“新冠日记”\n在疫情期间，没有人是一座孤岛\n“我们从未想过要为丢垃圾获得许可。\n我们从未想过要按照民防部的... ', '2015-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('游隼', 9025, '\n\n\n[英]J.A.贝克李斯本', '随笔', '浙江教育出版社', '达夫·库珀奖获奖作品\n纽约书评图书“经典重现”书系选书\n赫尔佐格“无赖电影学校”必读书目\n影响持续半个世纪的经典首次引进中国\n“猎人必须成为他所追捕的猎物。... ', '2015-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('爱与战争的日日夜夜', 9026, '\n\n\n[乌拉圭]爱德华多·加莱亚诺', '随笔', '百花文艺出版社', '拉美文豪加莱亚诺之获奖代表作\n了解其独异文学风格的最佳入口\n在拉美文学星空上，爱德华多·加莱亚诺是光芒独异的星辰，始终向下的立场，对于被遮蔽之历史、被绑架之... ', '2019-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('奥克诺斯', 9027, '\n\n\n[西班牙]路易斯·塞尔努达', '随笔', '人民文学出版社', '《奥克诺斯》是西班牙诗人塞尔努达的散文诗集。流亡国外的诗人追忆童年、秋日和故乡小城，在文字中重构一个透明的世界，达成自己对生命的理解、对永恒的渴望。\n在西班... ', '2017-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('悲伤与理智', 9028, '\n\n\n[美]约瑟夫·布罗茨基刘文飞', '随笔', '上海译文出版社', '在这部题材丰富、视界浩淼的散文集中，约瑟夫•布罗茨基开篇便用深沉内省的目光审视了自己在苏俄的早年经历以及随后去往美国的流亡生涯。接着，作者用惊人的博学探讨了... ', '2021-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('万事有心，人间有味', 9029, '\n\n\n汪曾祺', '随笔', '中国友谊出版公司', '本书是汪曾祺先生的散文精选集，特别收录入选初、高中语文教材以及高考辅助阅读的《端午的鸭蛋》《金岳霖先生》《北京的秋花》等经典篇目，一本书阅尽汪曾祺的散文精品... ', '2017-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('羊道三部曲(共3册)', 9030, '\n\n\n[中国]李娟', '随笔', '中信出版社', '“羊群在一整面山坡上弥漫开来，沿着平行着布满坡体的上百条弧线（那就是羊道）有序前行，丝丝入扣。” 新疆北部游牧地区的哈萨克牧民大约是这个世界上最后一支最为纯... ', '2021-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('一眨眼，算不算少年   一辈子，算不算永远', 9031, '\n\n\n余光中', '随笔', '百花洲文艺出版社', '本书精选余光中散文集21篇，共分为三个章节：选取了旅途见闻、旧时光景、天下之事三个角度。行走足迹、羁旅之思、对世间万事异乎寻常的关切敏感之情，都一一氤氲在作... ', '1999-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('温柔的夜', 9032, '\n\n\n三毛', '随笔', '北京十月文艺出版社', '她以一支笔坚持看守个人文字上的简单和朴素\n从遥远的撒哈拉到敦煌戈壁，她不随波逐流，也不诠释人生，只做生活的见证者\n她是我们心中浪漫、洒脱、真性情的永远的三毛... ', '2017-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('山川面目', 9033, '\n\n\n李琬', '随笔', '漓江出版社', '《山川面目》是青年诗人李琬的散文随笔集，包括三个部分：游记，涉及亚美尼亚、新疆、内蒙古等地的漫游和见闻；随笔，是她对民间文化、乡土世界的观察和记录；文艺评论... ', '2021-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('锦灰堆', 9034, '\n\n\n王世襄', '随笔', '生活·读书·新知三联书店', '《锦灰堆》一二卷收集了作者在80岁以前所写的大部分文章，计105篇，第三卷则以手书影印的形式选收了王世襄历年所作的诗词120首。全书计有线图234幅，黑白图... ', '2012-08-25', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('幸得诸君慰平生', 9035, '\n\n\n故园风雨前', '随笔', '清华大学出版社', '以为活着是为了收悉美。\n某公笑叹，什么是美？即“不知生活艰辛”。我点头，但转头还得给他补半句，“或佯作不知生活艰辛”。\n这本书就是这么个情况。 ', '2019-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('还是要相信', 9036, '\n\n\n陈鲁豫', '随笔', '花城出版社', '知名主持人陈鲁豫走心新作，一部极度坦诚的人生思考之书——涵盖6大主题，剖析55个问题，直击当下我们的焦虑与困惑；清醒、真挚、温暖；如闺蜜，亦如诤友，希望这本... ', '2010-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('羊道·前山夏牧场', 9037, '\n\n\n李娟', '随笔', '上海文艺出版社', '“羊道”系列是李娟与哈萨克牧民扎克拜妈妈一家共同生活、历经寒暑跋涉后，在几年时间内陆续写下的文字，共分三卷。这是第二卷，讲述的是在发生在前山夏牧场的故事。\n... ', '2017-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('克林索尔的最后夏天', 9038, '\n\n\n[德]赫尔曼·黑塞易海舟', '随笔', '天津人民出版社', '全书包含两个部分。《克林索尔的最后夏天》是黑塞创作于1919年的中篇小说。彼时一战刚刚结束，世界尚未从混乱中恢复，千万士兵、战俘和民众，从多年僵化统一的顺服... ', '2004-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('国王鞠躬，国王杀人', 9039, '\n\n\n[德]赫塔·米勒李贻琼', '随笔', '江苏人民出版社', '《国王鞠躬，国王杀人》是一部散文集，寓意丰富。作者运用“少数民族语言的独到性”，讲述了她在秘密警察的监督下，颠沛流离的灰暗生活。她在极权统治扭曲语言的现状下... ', '2011-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('前山夏牧场', 9040, '\n\n\n李娟', '随笔', '中信出版集团', '“羊群在一整面山坡上弥漫开来，沿着平行着布满坡体的上百条弧线（那就是羊道）有序前行，丝丝入扣。”\n新疆北部游牧地区的哈萨克牧民大约是这个世界上最后一支最为纯... ', '2018-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('留德十年', 9041, '\n\n\n季羡林', '随笔', '中国人民大学出版社', '1935年，青年学子季羡林赴德留学，开始了十年羁旅生涯。数十年后，学术泰斗季先生已近耄耋之年，忆及往昔，遂写下一部《留德十年》。不书讯时间的脉络，记述了先生... ', '2004-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('反与正·婚礼集·夏天集', 9042, '\n\n\n(法)加缪郭宏安', '随笔', '译林出版社', '本书为《加缪文集》第三卷，散文集《反与正》叙述了童年生活；《婚礼集》和《夏天》表述了对生活的热爱和对死亡的恐惧；附《瑞典演说》是1957年作者荣获诺贝尔文学... ', '2006-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('山河故人', 9043, '\n\n\n汪曾祺', '随笔', '天津人民出版社', '本书完备地收录了汪曾祺怀人忆旧系列散文。在书中，我们将认识作者儿时的家庭成员和学校教员、故乡的街巷店铺和坊间奇人，还有昔日西南联大那些气质各异的师生以及在战... ', '2016-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('务虚笔记', 9044, '\n\n\n史铁生', '日本文学', '南海出版公司', '《务虚笔记》是由二十二个段落合成的长篇小说，叙述上世纪50年代以来的社会嬗变带给残疾人C、画家Z、女教师O、诗人L、医生F、女导演N等一代人的影响。本书是史... ', '2006-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('猎人笔记', 9045, '\n\n\n屠格涅夫冯春', '日本文学', '上海译文出版社', '《猎人笔记》是一部通过猎人的狩猎活动，记述十九世纪中叶俄罗斯农村生活的随笔集。随着屠格涅夫充满优美笔调的叙述，俄罗斯的大自然风光、俄罗斯人民的风俗习惯、地主... ', '2003-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('人生最美是清欢', 9046, '\n\n\n林清玄', '日本文学', '北京十月文艺出版社', '《人生最美是清欢》（林清玄经典散文集，执笔45周年白金纪念版！）\n在复杂的世界里，做一个简单的人，以清静心看世界，以欢喜心过生活。首次手绘古风配图，精美四色... ', '2006-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('流言', 9047, '\n\n\n张爱玲', '日本文学', '北京十月文艺出版社', '《流言》是张爱玲的随笔散文集，可以说是大珠小珠满玉盘。张爱玲称爱默生“警句很多”。在这本书中我们倒可以看到张爱玲满篇的警句佳句。如“上海人是传统的中国人加上... ', '2017-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('命若琴弦', 9048, '\n\n\n史铁生', '随笔', '江苏文艺出版社', '本书包括：午餐半小时、没有太阳的角落、我的遥远的清平湾、关于詹牧师的报告文学等内容。 ', '2006-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('朱自清散文集', 9049, '\n\n\n朱自清', '随笔', '西苑出版社', '作为一位散文大家，朱自清以他独特的美文艺术风格，为中国现代散文增添了瑰丽的色彩，为建立中国现代散文全新的审美特征，树立了“白话美文的模范”。朱自清是一位文化... ', '2012-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('春牧场', 9050, '\n\n\n李娟', '随笔', '中信出版集团', '“羊群在一整面山坡上弥漫开来，沿着平行着布满坡体的上百条弧线（那就是羊道）有序前行，丝丝入扣。”\n新疆北部游牧地区的哈萨克牧民大约是这个世界上最后一支最为纯... ', '2014-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('上学记', 9051, '\n\n\n何兆武口述、文靖撰写', '随笔', '生活·读书·新知三联书店', '何兆武教授的这部口述浓缩了20世纪中国知识分子的心灵史。它叙述的尽管只是1920年代-1940年代末不足30年间他学生时期的陈年往事，却蕴含着一个饱经沧桑的... ', '2021-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('中国文脉', 9052, '\n\n\n余秋雨', '随笔', '长江文艺出版社', '《中国文脉》是继《文化苦旅》之后，余秋雨教授最重要的作品，也是当今中国当代文史领域最重要的作品之一。余秋雨教授感叹当前“文脉既隐，小丘称峰；健翅已远，残羽称... ', '2017-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('人间食粮', 9053, '\n\n\n(法)纪德李玉民', '随笔', '上海译文出版社', '安德烈•纪德（1869—1951）是法国二十世纪最重要的作家之一，也是法国乃至整个现代西方文学史、思想史上最重要的人物之一，因其“内容广博和艺术意味深长的作... ', '2020-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('太多值得思考的事物：索尔•贝娄散文选1940-2000', 9054, '\n\n\n[美]索尔•贝娄李纯一、索马里', '随笔', '人民文学出版社', '从芝加哥到纽约、巴黎、马德里、托斯卡纳……从经历美国经济“大萧条”的少年到诺贝尔文学奖得主，索尔•贝娄散文精选首次出版，讲述自己奇妙的人生旅程\n——————... ', '2009-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我为你洒下月光', 9055, '\n\n\n简媜', '随笔', '九州出版社', '★当代华语文坛散文大家简媜写作三十年纪念之作\n★这样的书，一生只能写一本，为致敬，也是向过去的一场告别\n★如果《水问》是青春之书，《女儿红》是女性之书，《红... ', '2011-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('一个知识女性的思考系列', 9056, '\n\n\n[英]蕾切尔·卡斯克', '随笔', '中信出版集团', '编辑推荐：\n★蕾切尔·卡斯克历时四年完成的小说三部曲，每一本都受到全球各大权威媒体的重磅推荐！\n《边界》被评为《纽约时报》2015年度十大图书，《卫报》21... ', '2020-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('送你一匹马', 9057, '\n\n\n三毛', '随笔', '北京十月文艺出版社', '《送你一匹马》收录了三毛回台定居后发表的一系列文章。\n本卷与三毛早期作品风格不同，这一阶段的创作主要是出于对生命的感悟与体认而发表的心情文字。在字里行间中透... ', '2013-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('普罗旺斯的一年', 9058, '\n\n\n[英]彼得•梅尔王春', '随笔', '南海出版公司', '* 一杯美酒，一把舒服的椅子，聆听质朴时光的潺潺流淌。\n* 此地放眼是葡萄树和山峦，生活朴素平淡。在这里，买橄榄油、采樱桃、找松露，都是大事。寻找美食，是生... ', '1997-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('散文课', 9059, '\n\n\n张怡微', '随笔', '华东师范大学出版社', ' 复旦大学创意写作MFA专业散文课\n 国内第一本对现代散文进行条分缕析的创意写作指南\n 现代散文以自然的语言形态，照亮我们连绵不断的生命经验与复杂情感... ', '2015-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('林清玄散文精选', 9060, '\n\n\n林清玄', '随笔', '长江文艺出版社', '在林清玄的创作生涯中，除了后期最负盛名的“菩提系列”十书，其早期的散文作品，也因文笔清丽、情感醇厚，而深受读者欢迎，屡获台湾各项文学大奖。\n因此，本书除精选... ', '2005-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我的精神家园', 9061, '\n\n\n王小波', '随笔', '文化艺术出版社', '一九九七年六月，王小波逝世两个月后，他的杂文自选集《我的精神家园》出版。这是第一版的封面。相比于2002版出版的《我的精神家园》纪念版来说，这一版本没有纪念... ', '2010-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('树犹如此', 9062, '\n\n\n白先勇', '随笔', '广西师范大学出版社', '《树犹如此》是白先勇的散文自选集，主要收录他回忆个人经历、亲友交往的文章。其中纪念亡友的《树犹如此》将至深痛楚沉淀六年，被称为“以血泪、以人间最纯真的感情去... ', '1999-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('灵魂的事', 9063, '\n\n\n史铁生', '随笔', '百花文艺出版社', '《灵魂的事》是史铁生关于生命、爱情和信仰的沉思的散文集。\n史铁生是当代中国最令人敬佩的作家之一。他的写作与他的生命完全同构在了一起，在自己的“写作之夜”；史... ', '2016-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('干校六记', 9064, '\n\n\n杨绛', '随笔', '生活·读书·新知三联书店', '干校，本来是对公务人员和事业单位人员进行培训的地方，但在“文革”中，却赋予了它新的定语——“五七”，新的含义——对上述人员中的大多数进行“劳改”。杨绛先生在... ', '2000-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('雅舍小品', 9065, '\n\n\n梁实秋', '随笔', '文化艺术出版社', '本书收录《雅舍》、《孩子》、《音乐》、《信》、《女人》、《男人》、《洋罪》、《谦让》、《衣裳》、《结婚典礼》、《第六伦》、《客》等作品。 ', '2017-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('醒来觉得甚是爱你', 9066, '\n\n\n朱生豪', '随笔', '哈尔滨出版社', '《醒来觉得甚是爱你：朱生豪情书集》是中国著名诗人、翻译家朱生豪的情书精选集。朱生豪自1933年与宋清如相遇，至1944年英年早逝，10年之中，鸿雁不绝，留下... ', '2014-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('霜冷长河', 9067, '\n\n\n余秋雨', '随笔', '作家出版社', '《霜冷长河》自序\r\n\r\n余秋雨 \r\n\r\n几年前，有一次我到北京一位朋友那里却玩，见到一位异人。他见到我，双目炯炯地逼视良久，便说：“这位先生，你从小是不是产... ', '2017-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('稻草人手记', 9068, '\n\n\n三毛', '随笔', '北京十月文艺出版社', '她以一支笔坚持看守个人文字上的简单和朴素\n从遥远的撒哈拉到敦煌戈壁，她不随波逐流，也不诠释人生，只做生活的见证者\n她是我们心中浪漫、洒脱、真性情的永远的三毛... ', '2017-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('人间草木', 9069, '\n\n\n汪曾祺', '随笔', '天津人民出版社', '《人间草木》是汪曾祺写他的旧人旧事、旅行见闻、各地风土人情、花鸟虫鱼的经典散文集，字里行间充分流露出他对凡人小事和乡土名俗的深深眷恋和对旧日生活情景的缅怀。... ', '2012-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('另一种美', 9070, '\n\n\n[波]亚当·扎加耶夫斯基', '随笔', '花城出版社', '本书是波兰著名作家亚当•扎加耶夫斯基的散文随笔集，书中汲取了成长回忆录、人物速写、书摘、沉思性警句、小品文，以及诗辩等多种文学体裁的长处，形式多变。利沃夫、... ', '2006-02-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('平如美棠', 9071, '\n\n\n饶平如', '随笔', '广西师范大学出版社', '这是饶平如一生的故事。\n他不是一个想打仗的人，但他还是义无反顾去打仗了。又因为和美棠在一起，他最终厌倦了战争，想要回家。\n六十年的相守历尽坎坷，命运让他们长... ', '2012-08-25', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('行者无疆', 9072, '\n\n\n余秋雨', '随笔', '长江文艺出版社', '15年前余秋雨开始以长途旅行方式实地考察文化。他从国内走到国外，从中华文明走到了其他文明。这期间，他以《文化苦旅》和《山河之书》作为考察中华文明的记录，以《... ', '2012-08-25', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('生活的艺术', 9073, '\n\n\n林语堂越裔汉', '随笔', '陕西师范大学出版社', '《生活的艺术》是林语堂旅美专事创作后的第一部书，也是继《吾国与吾民》之后再获成功的又一英文作品。该书于1937年在美国出版，次年便居美国畅销书排行榜榜首达5... ', '2003-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('羊道·深山夏牧场', 9074, '\n\n\n李娟', '随笔', '上海文艺出版社', '“羊道”系列是李娟与哈萨克牧民扎克拜妈妈一家共同生活、历经寒暑跋涉后，在几年时间内陆续写下的文字，共分三卷。这是第三卷，讲述的是在发生在深山夏牧场的故事。\n... ', '2017-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('羊道·春牧场', 9075, '\n\n\n李娟', '随笔', '上海文艺出版社', '“羊道”系列是李娟与哈萨克牧民扎克拜妈妈一家共同生活、历经寒暑跋涉后，在几年时间内陆续写下的文字，共分三卷。这是第一卷，讲述的是在发生在春牧场的故事。\n在李... ', '2019-07-16', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('左手的掌纹', 9076, '\n\n\n余光中', '随笔', '江苏文艺出版社', '“右手写诗，左手写散文”，这是文坛对台湾著名学者余光中公认的形容。而今，余老又用他的左手为读者奉献上一道精神大餐，散文集《左手的掌纹》选其作品五十多篇，有短... ', '2002-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('雨季不再来', 9077, '\n\n\n三毛', '随笔', '北京十月文艺出版社', '她以一支笔坚持看守个人文字上的简单和朴素\n从遥远的撒哈拉到敦煌戈壁，她不随波逐流，也不诠释人生，只做生活的见证者\n她是我们心中浪漫、洒脱、真性情的永远的三毛... ', '2011-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('一生自在', 9078, '\n\n\n季羡林', '随笔', '江苏凤凰文艺出版社', '《一生自在》一书，是一本体现季羡林自在智慧的作品。\n全书共有五章，分别是：\n“书卷伴青灯，足以慰平生”——关于读书启蒙的书中自在；\n“从容坦荡，心装万物”—... ', '1997-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('千年一叹', 9079, '\n\n\n余秋雨', '随笔', '作家出版社', '余秋雨作为特邀嘉宾，参与了香港凤凰卫视组织的大型跨国采访报道活动——“千禧之旅”，过程中所写的日记及散文在国内外多家报纸同步发表后引起较大反响。《千年一叹》... ', '2018-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('温柔的夜', 9080, '\n\n\n三毛', '随笔', '北京出版集团公司,北京十月文艺出版社', '此篇承袭《撒哈拉的故事》的风格，形成一种舒缓、沉静的调子，是三毛沙漠故事系列中登峰造极之作。\n阅遍种种人情冷暖之后，溢于三毛笔端的依然是对大千世界的真挚动人... ', '2009-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('置身于苦难与阳光之间', 9081, '\n\n\n（法）加缪杜小真', '随笔', '上海三联书店', '二战后法国有两位号称“精神领袖”的人物，一是萨特，一是加缪。加缪还被称为“法国的良心”，他的散文素以散淡、朴素又寓意深长著称。本书选录了《反与正》和《反叛者... ', '2010-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('偷书贼', 9082, '\n\n\n[澳]马库斯·苏萨克陶泽慧', '随笔', '北京十月文艺出版社', '《偷书贼》是澳大利亚知名作家马库斯•苏萨克的长篇小说代表作，它像《解忧杂货店》和《追风筝的人》一样，可以战胜孤独和恐惧，拯救身陷绝望的人，遇到这些书，是我们... ', '2015-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('精神明亮的人', 9083, '\n\n\n王开岭', '随笔', '书海出版社', '本书收入王开岭最具标志性的诗性散文和思想随笔。在思想界，他被誉为新生代的旗帜人物：在文学界，他被视为优美的灵魂书写者。其作品大量涌现在各类文选、年度排行榜、... ', '2017-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('哥伦比亚的倒影', 9084, '\n\n\n木心', '随笔', '广西师范大学出版社', '《哥伦比亚的倒影》是木心的第一部简体中文版作品，内中选编《九月初九》、《哥伦比亚的倒影》、《上海赋》等最能表现木心行文风格的散文13篇，并全文刊印1986年... ', '2015-02-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('山居杂忆（插图精装版）', 9085, '\n\n\n高诵芬、徐家祯', '随笔', '长江文艺出版社', '▻ 编辑推荐 ◅\n►一个大家闺秀的百年家族记忆 。\n►初版绝迹十年后再度重磅面世 。\n►新增100余幅珍贵老照片，3万余字注疏，惊现百年生活场景 。\n►寻找... ', '1995-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('记一忘三二', 9086, '\n\n\n李娟', '随笔', '中华书局', '《记一忘三二》是作家李娟的新作。书写近几年的点滴生活，有趣有味。\n曾在《南方周末》、《文汇报》等开设专栏，并出版散文集《九篇雪》、《我的阿勒泰》、《阿勒泰的... ', '2011-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('菊次郎与佐纪', 9087, '\n\n\n[日]北野武陈宝莲', '随笔', '译林出版社', '【日本殿堂级导演北野武暖心大作，笑中藏泪，细述他与他的“超级阿爸阿妈”】\n•电影大师北野武，笑谈贫寒童年，温情讲述别样成长故事。\n•出身底层，北野三兄弟却能... ', '2014-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('惶然录', 9088, '\n\n\n（葡萄牙）费尔南多·佩索阿', '随笔', '上海文艺出版社', '这是葡萄牙作家费尔南多·佩索阿晚期随笔结集，都是“仿日记”片断体。它是作者的代表之一，是一部曾经长期散佚的作品，后来由众多佩索阿的研究专家们搜集整理而成。\n... ', '2010-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我的宝贝', 9089, '\n\n\n三毛', '随笔', '北京十月文艺出版社', '《三毛全集:我的宝贝》收录三毛走遍万水千山收集的86件宝贝的来历故事及全部精美照片。这些宝贝的价格也许不能用金钱来衡量，但是作者却深深地爱着它们……所有宝贝... ', '2015-02-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('你的孤独，虽败犹荣', 9090, '\n\n\n刘同', '随笔', '中信出版社', '孤独之前是迷茫，孤独之后是成长\n“很长一段日子里，我靠写东西度过了太多的小无聊，伪伤感，假满足与真茫然 。我在意细节，算敏感。但知道体谅，算善良。我说喜欢便... ', '2007-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('城门开', 9091, '\n\n\n北岛', '随笔', '生活·读书·新知三联书店', '生于北京，长于北京的作者，阔别家乡十三年后重回北京，发现他在自己的故乡成了异乡人，仿佛到了一座陌生的城市。于是他便要“重建我的北京”——用他文字召回北京消失... ', '2011-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我们生活在巨大的差距里', 9092, '\n\n\n余华', '随笔', '北京十月文艺出版社', '《我们生活在巨大的差距里》是余华自2003年以来的首部杂文集。\n十年来他走遍世界，以亦庄亦谐的笔锋将观察到的社会、时事、文化等现象一一记录剖析，在日常生活的... ', '2011-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('缘缘堂随笔', 9093, '\n\n\n丰子恺', '随笔', '天津教育出版社', '《缘缘堂随笔》是丰子恺先生的主要散文选。1927年，丰子恺皈依弘一法师，在江湾永义里的寓所举行了仪式。丰子恺并请弘一法师为自己的住所取名。尊弘一嘱，丰子恺在... ', '2015-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('雨季不再来', 9094, '\n\n\n三毛', '随笔', '北京十月文艺出版社', '当三毛还是二毛的时候，她写下了《雨季不再来》，这些在她17~22岁之间所发表的文字，真实地呈现出从青涩敏感的二毛，蜕变为智慧成熟的三毛的成长的过程。而《雨季... ', '2017-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('稻草人手记', 9095, '\n\n\n三毛', '随笔', '北京出版集团公司,北京十月文艺出版社', '《稻草人手记》记录的是三毛定居加纳利岛后生活中的点点滴滴，语言朴实、简单，其中的情趣与无奈，朴实而谐趣，令人笑叹。也许，正是这样简简单单的生活，才给了三毛无... ', '2011-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('朝花夕拾', 9096, '\n\n\n鲁迅', '随笔', '天津人民出版社', '曾经在陈丹青先生《鲁迅是谁？》的演讲中听到一个别致的观点，他说“鲁迅的被扭曲，是现代中国一桩超级公案”，对鲁迅以“政治上的正确”给予他的作品褒扬、抬高，不可... ', '2004-11-03', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('你是人间四月天', 9097, '\n\n\n林徽因', '随笔', '浙江文艺出版社', '《你是人间四月天》以梁从诫编纂的《林徽因集》、《新月诗选》等为编辑底本，精心挑选、收录最能突显其性格气质的散文7篇，包括《悼志摩》《窗子以外》等；小说6篇，... ', '2020-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('寻找家园（增订版）', 9098, '\n\n\n高尔泰', '随笔', '北京十月文艺出版社', '《寻找家园》是一代学人的心灵史，是关于苦难的艺术之书。书中作品沉重如山，灵动如水，是当代散文最美的收获。本书是《寻找家园》是增订新版，是传世经典，值得一看。... ', '2008-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('寻找家园', 9099, '\n\n\n高尔泰', '随笔', '花城出版社', '我的故乡高淳，位于江苏省西南端与安徽省交界的地方，恰好是“吴头楚尾”。地势东省交界的寺主， 恰好是“吴头楚尾”。地势东高西低。东部是茅山山脉和天目山山脉的衔... ', '2011-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('花未眠', 9100, '\n\n\n[日]川端康成陈德文', '随笔', '时代文艺出版社', '日本的美，风靡世界，川端康成功不可没。他以美为\"信仰\"，用唯美文字拓展美学疆域。其思想逐渐成熟，零星展现在诸多随笔之中，一般读者难以得窥全貌。学者型翻译名家... ', '2016-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('青灯', 9101, '\n\n\n北岛', '随笔', '江苏文艺出版社', '《青灯》是诗人北岛的散文集，收录了他新近完成的十一篇作品。第一部分是忆念，主角是熊秉明、蔡其矫、魏斐德、冯亦代等故人，青灯素帐，烛影惶惶；第二部分则是游历，... ', '2004-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('水问', 9102, '\n\n\n简媜', '随笔', '文化艺术出版社', '《水问》为简媜第一本书，以清纯的少女心怀叙说大学校园里外的人生幻化，笔触自然且富创造意蕴。\n《水问》共分六卷，始于《花诰》，终于《化音》。其中每卷以卷首语拈... ', '2006-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('野草', 9103, '\n\n\n鲁迅', '随笔', '天津人民出版社', '【内容简介】\n鲁迅曾经说过，自己一生的哲学都在《野草》里了。新版《野草》精选了74篇短小却意味深长的诗文，全书分四个部分，开篇完整再现《野草》，其余三个部分... ', '1999-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('飞鸟集', 9104, '\n\n\n[印]罗宾德拉纳德·泰戈尔', '随笔', '哈尔滨出版社', '《飞鸟集》是泰戈尔的代表作之一，也是世界上最杰出的诗集之一。白昼和黑夜、溪流和海洋、自由和背叛，都在泰戈尔的笔下合而为一，短小的语句道出了深刻的人生哲理，引... ', '2014-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('诗经', 9105, '\n\n\n孔丘编订', '随笔', '北京出版社', '这本《诗经》收录全部305篇，每篇诗歌都配有相应的解释及译文，使读者更容易理解。 ', '2015-02-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('海子的诗', 9106, '\n\n\n海子', '随笔', '人民文学出版社', '本书收录了已故诗人海子的诗作精华，其诗以独特的风格深受读者的喜爱，从这些诗中反映出诗人那对于一切美好事物的眷恋之情，对于生命的世俗和崇高的激动和关怀。 ', '2020-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('二十首情诗和一首绝望的歌', 9107, '\n\n\n[智利]巴勃罗·聂鲁达陈黎', '随笔', '南海出版公司', '【聂鲁达豆瓣小站：http://site.douban.com/neruda/ 】\n生命中只有两样东西不可缺少：诗歌和爱情。送一首诗给你的爱情！\n★ 诺贝尔... ', '2021-02-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('月光落在左手上', 9108, '\n\n\n余秀华', '随笔', '广西师范大学出版社', '编辑推荐\n余秀华的诗歌，诉诸的是诗本身神秘非理性的逻辑，自有其妙。雄辩的诗歌向来为中国当代诗推崇，而余秀华的诗放弃辩论，放弃自圆其说，甚至放弃结论，因此与读... ', '2016-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('大山里的小诗人', 9109, '\n\n\n“是光”的孩子们', '随笔', '江苏凤凰文艺出版社', '【内容简介】\n·\n《大山里的小诗人》是由公益组织“是光诗歌”发起，果麦文化编辑制作的乡村儿童配图诗集，全书共收录了120首儿童手抄诗，配有73幅随书插画，由... ', '2012-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('在光与万物背后', 9110, '\n\n\n[德]马蒂亚斯·波利蒂基胡蔚等', '随笔', '未读·贵州人民出版社', '《在光与万物背后》选自马蒂亚斯·波利蒂基的《诗歌总集2017—1987》，诗人为中国读者精选出81首诗，按主题分为自然之诗、城市之诗、爱情之诗、生活之诗与远... ', '2021-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('万物静默如谜', 9111, '\n\n\n［波］维斯拉瓦·辛波斯卡陈黎', '随笔', '湖南文艺出版社', '我偏爱写诗的荒谬，胜于不写诗的荒谬。\n——辛波斯卡\n1996年诺贝尔文学奖得主辛波斯卡，是当代最为迷人的诗人之一，享有“诗界莫扎特”的美誉。她的诗别具一格，... ', '2021-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('荒原', 9112, '\n\n\n[英]T·S·艾略特裘小龙、汤永宽', '随笔', '上海译文出版社', '艾略特被称为“世界诗歌漫长历史中一个新阶段的带领人”，是英语诗歌界“最有影响的诗人之一”。本书全面收录了T·S·艾略特的诗歌。他在诗歌中通过用典暗示、启发读... ', '2016-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('提琴与坟墓', 9113, '\n\n\n[西]费德里科·加西亚·洛尔迦', '随笔', '雅众文化|北京联合出版公司', '※继塞万提斯之后最为世界所熟知的西班牙作家加西亚•洛尔迦诗歌精选\n※他是被全世界拥抱的诗人，甜蜜如雪地里一声啜泣\n※莱昂纳德•科恩和帕蒂•史密斯都从他的作品... ', '2011-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('兰波评传：履风的通灵人与盗火者', 9114, '\n\n\n[法]伊夫·博纳富瓦杜卿', '随笔', '上海社会科学院出版社', '- 编辑推荐 -\n★法国当代举足轻重的世界级诗人、《声音中的另一种语言》作者伊夫·博纳富瓦动情抒写，诗人写诗人的典范之作，不仅是见解独到、风格别具的兰波传记... ', '1998-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('直到世界反映了灵魂最深层的需要', 9115, '\n\n\n[美]露易丝·格丽克柳向阳', '随笔', '上海人民出版社', '•2020年诺贝尔文学奖得主露易丝·格丽克作品。诺奖授奖词：她精准的诗意语言所营造的冷峻之美，让个体的存在具有普遍性。\n• 露易丝·格丽克是诺贝尔文学奖、普... ', '2009-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('恶之花', 9116, '\n\n\n[法]波德莱尔钱春绮', '随笔', '人民文学出版社', '全书分为“忧郁与理想”、“巴黎风光”、“酒”、“恶之花”、“叛逆”、“死亡”和“增补诗”七部分。在诗人的笔下，巴黎风光阴暗而神秘，吸引他目光的是被社会抛弃的... ', '2019-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('唐诗三百首', 9117, '\n\n\n蘅塘退士、陈婉俊', '随笔', '中华书局', '《唐诗三百首》的旧注本亦有许多种，其中以陈婉俊的补注本简明精当，流行最广。她的注解除简介作者生平外，比较注重语词名物的诠释，博引旁征，字梳句栉，对今天的读者... ', '2017-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('海子诗全集', 9118, '\n\n\n海子著、西川编', '随笔', '作家出版社', '《海子诗全集》收录迄今为止所有发现的海子文学作品。全书以上海三联书店1997年出版的《海子诗全编》为底本，依照时间与类别结合的方式，收入海子的抒情短诗、长诗... ', '2009-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('诗人十四个', 9119, '\n\n\n黄晓丹', '随笔', '北京联合出版公司', '十四位古代诗人和一个现代闯入者\n一场始于1600年前的诗歌沙龙\n·\n七堂围绕“春天”意象与“青春”经验的文学课，\n以现代心理学解读古代诗人，\n诗与心灵自由飞... ', '1998-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('孩子们的诗', 9120, '\n\n\n果麦编', '随笔', '浙江文艺出版社', '书中有灵气的诗句包括但不限于以下：\n*我的眼睛／很小很小／有时遇到心事／就连两行泪／也装不下——陈科全， 八岁\n*灯把黑夜／烫了一个洞——姜二嫚，七岁\n*春... ', '2021-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我的孤独是一座花园', 9121, '\n\n\n[叙利亚]阿多尼斯薛庆国', '随笔', '译林出版社', '《我的孤独是一座花园》是阿多尼斯作品的首个中译版，收录了诗人从上世纪50年代“最初的诗”到2008年的最新作品，时间跨越半个世纪。 ', '2020-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('顾城的诗', 9122, '\n\n\n顾城', '随笔', '人民文学出版社', '《顾城的诗》是人民文学出版社1986年版《黑眼睛》的再版，在原书的基础上做了相应的删节和补充。其中有些诗是第一次发表。突出了一个时期的重点和特点，稍兼顾全面... ', '2019-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('灰烬的光辉', 9123, '\n\n\n[德]保罗·策兰王家新', '随笔', '广西师范大学出版社', '集结保罗·策兰近400篇诗作、散文、书信精华，翻译家王家新30年心血译作\n20世纪最重要、卓异的德语诗人保罗·策兰经典重磅作品\n纯粹译丛之“王家新译文系列”... ', '2020-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('姜二嫚的诗', 9124, '\n\n\n姜二嫚', '随笔', '浙江文艺出版社', '“太好了/我比姐姐/多个姐姐”——姜二嫚，6岁\n“灯把黑夜/烫了一个洞”——姜二嫚，7岁\n“如果有谁对我说/我爱你/这绝对绝对不是原创/原创在妈妈那里”——... ', '2021-05-05', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('夜的命名术', 9125, '\n\n\n[阿根廷]阿莱杭德娜•皮扎尼克', '随笔', '作家出版社', '《夜的命名术：皮扎尼克诗合集》翻译自西班牙语原版《皮扎尼克诗全集》，收录了皮扎尼克生前以“阿莱杭德娜•皮扎尼克”署名结集出版的全部诗作，以其六本诗歌单行本为... ', '2003-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('李金发诗全编', 9126, '\n\n\n李金发', '随笔', '四川文艺出版社', '● 李金发诗作首次集结，中国象征派诗歌研究的“参考书”\n● 纪念中国象征派诗歌开创者李金发诞辰120周年\n● 中国现当代文学史上的丰碑，备受蔡元培、朱自清、... ', '2017-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('数星星的夜', 9127, '\n\n\n尹东柱全勇先、全明兰', '随笔', '江苏凤凰文艺出版社', '【编辑推荐】\n☆数十年来，在中国、朝鲜半岛、日本，他的诗歌朗诵会及纪念活动源源不绝，似乎每个时代都需要他的诗歌予以见证。他长眠于其出生地中国延边，他的墓地常... ', '2019-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('二十首情诗与绝望的歌', 9128, '\n\n\n聂鲁达李宗荣', '随笔', '中国社会科学出版社', '本书收有“女人的身体”、“光笼罩你”、“我记得你往日的样子”、“陶醉在松林中”等诗歌。 ', '2020-12-25', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('海子的诗', 9129, '\n\n\n海子', '随笔', '江西人民出版社', '海子，精英和大众都爱他，官方和百姓都爱他。海子的诗，还存在于娄烨的电影、高晓松晓说、周云蓬《九月》，俞敏洪演讲、撒贝宁和白岩松的朗诵、柴静采访、汪峰《流浪》... ', '2021-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('炸裂志', 9130, '\n\n\n陈年喜', '随笔', '太白文艺出版社', '本书是丛书“我的诗篇——当代工人诗歌精选”的其中一部，该丛书精选六位打工诗人影响广泛、具有代表性的精品诗作，保留《我的诗篇》纪录片中的诗歌。本书作者陈年喜做... ', '2020-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我的心迟到了', 9131, '\n\n\n[葡]费尔南多·佩索阿姚风', '随笔', '浙江文艺出版社', '葡萄牙诗人佩索阿情诗集结\n北岛、舒婷、西川、阿乙诚意推荐\n葡萄牙“圣地亚哥宝剑勋章”获得者、澳门大学葡文系教授，诗人姚风葡语直译\n葡萄牙诗人佩索阿疯狂地爱过... ', '2003-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('因思念而沉着', 9132, '\n\n\n巴哑哑', '随笔', '北京联合出版公司', '我负责制造沉默，如果已有太多声音。\n历时15年沉潜之作，巴哑哑首部公开出版诗集。\n————\n“它们是下过的雨，开过的花，被吃掉的果实，生活过的生活。”\n《因... ', '2020-09-01', -1, 30000, '泰山');
