INSERT INTO `books` VALUES ('我坐在火山的最边缘', 9160, '\n\n\n包慧怡', '随笔', '上海人民出版社', '本书收录了包慧怡2005—2015年间的代表诗作，是作者首部诗集的修订版。\n---\n★ 全新修订版收入十首未发表诗作以及一篇后记。\n★ 包慧怡是研究冷门“绝... ', '2018-05-01', -1, 30000, '泰山');
