INSERT INTO `books` VALUES ('新的一天', 9167, '\n\n\n许立志著、秦晓宇编选', '随笔', '作家出版社', '2014年9月30日下午近两点，九零后许立志从深圳龙华一座大厦的十七层一跃而下，10月1日0点0分，他预设了定时发送的一条微博“新的一天”，准时发布于他已辞... ', '2021-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('新生', 9168, '\n\n\n[意]但丁石绘、李海鹏', '随笔', '漓江出版社', '《新生》是但丁的第一部作品，抒写诗人对贝阿特丽采的爱情，具有回忆录和忏悔录性质。作品用散文诗叙述故事情节，将31首抒情诗串联起来。诗中洋溢着但丁对生命的执着... ', '2013-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('唐宋词十七讲', 9169, '\n\n\n叶嘉莹', '随笔', '北京大学出版社', '一部完整的唐宋词史。本书为国学大家叶嘉莹教授应辅仁大学校友会之邀所做的唐宋词系列讲座纪录。\n《唐宋词十七讲》为作者唐宋词系列讲座的讲演记录，内容是《唐宋词名... ', '2021-02-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('内向游戏', 9170, '\n\n\n愚公子', '随笔', '北京联合出版公司', '每日挥刀五百下，一个人，一年\n是诗，是漫画，也是动画\n内向主义者，在静默的表象之下，有波涛万顷\n莱布尼茨说：“单子没有窗户。”每个人都是孤独的个体，犹如浩茫... ', '2020-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('唐诗鉴赏辞典（新一版）', 9171, '\n\n\n俞平伯', '随笔', '上海辞书出版社', '《唐诗鉴赏辞典》是国内文艺类鉴赏辞典的发轫制作。它首创的融文学赏析和工具书于一体的编写体例，已成为中国文学鉴赏辞典的固有模式。《唐诗鉴赏辞典》第一版于198... ', '2015-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('三行集', 9172, '\n\n\n张新颖', '随笔', '上海文艺出版社', '张新颖教授得到朋友送的一个小本子，一页只能写三四行，出于敬惜字纸，他以三行为限，写自由的句子。“风吹到句子之间/ 风吹词语/ 风吹到旷野和字的笔画之间”。这... ', '2019-01-01', -1, 30000, '泰山');
