INSERT INTO `books` VALUES ('繁星春水', 9176, '\n\n\n冰心', '随笔', '人民文学出版社', '《繁星春水》内容简介：《繁星春水》是冰心的诗集，从中不难看出冰心纯美的文字、细腻的笔致。让你宛如重温儿时的梦，和她一起在野外的夜晚躺在草地上数星星。\n《繁星... ', '2020-12-15', -1, 30000, '泰山');
