INSERT INTO `books` VALUES ('寺山修司少女诗集', 9191, '\n\n\n[日]寺山修司彭永坚', '随笔', '湖南文艺出版社', '眼泪\n是人类自己做出来的\n最小的海\n——寺山修司\n————————\n被遗忘的少女，住在港湾红色的小屋里。她所做的事，只是每日夕阳西下之时前往海边，录下海水的... ', '2020-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('顾城的诗 顾城的画', 9192, '\n\n\n顾城', '随笔', '江苏文艺出版社', '《顾城诗歌精品:顾城的诗 顾城的画(插图本)》主要内容：顾城是一位在当代诗歌史上具有重要影响的诗人，他的创作是当代诗歌研究的重要对象这一。《顾城诗歌精品:顾... ', '2018-01-15', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('杜甫', 9193, '\n\n\n洪业曾祥波', '随笔', '上海古籍出版社', '“诗圣”杜甫何以荣膺“中国最伟大的诗人”之称号？洪业身处日寇囹圄，欲以杜甫诗晨祷夕诵；自述平生以杜诗慰其大悲大喜。后寓居大洋彼岸，用英文写就《杜甫：中国最伟... ', '2004-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('傍晚降雨', 9194, '\n\n\n吕德安雅众文化', '随笔', '北京联合出版公司', '汉语诗歌的沉潜之作，倾心于微物之神\n第三代诗歌代表诗人吕德安四十年诗歌创作集粹\n“有谁像我这样躺卧在天空下，起伏着，像尘土。”\n【编辑推荐】\n被称为“中国的... ', '2021-02-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我们生活的故事', 9195, '\n\n\n[美]马克·斯特兰德桑婪', '随笔', '湖南文艺出版社', '马克·斯特兰德，影响一代美国诗人的桂冠诗人，一生获奖无数，其诗歌被翻译成30多种语言。他被称为“深沉的异化哀悼者”，他机敏、克制，以深刻的智慧讲述我们被遮蔽... ', '2017-02-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('沙与沫', 9196, '\n\n\n卡里・纪伯伦、廖欣林志豪', '随笔', '哈尔滨出版社', '《沙与沫》是纪伯伦最著名的作品之一，作者以自然景物“沙”、“泡沫”为比喻，寓意着人在社会之中如同沙之微小，事物如同泡沫一般的虚幻。\n    整部诗集内容富有... ', '2021-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('藏地诗篇', 9197, '\n\n\n张子选', '随笔', '四川文艺出版社', '藏地诗人张子选代表作！让你苦苦等了40年，一部献给人间的诗集！大冰3500字深情推荐：“人间再不值得，他的诗也值得爱！”文艺青年、大中学生口碑推荐！读他的诗... ', '2010-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('春秋来信', 9198, '\n\n\n张枣', '随笔', '北京十月文艺出版社', '《春秋来信》是著名诗人张枣生前唯一出版的诗集。本书的篇目由诗人生前亲自编定，收录了他最重要的代表作品，一共六十三首。凭借这本诗集，张枣为汉语贡献了独特的美学... ', '2016-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('龟岛：斯奈德诗集', 9199, '\n\n\n[美]加里·斯奈德柳向阳', '随笔', '雅众文化|北京联合出版公司', '【编辑推荐】\n※垮掉派代表、禅宗诗人、“达摩流浪者”原型斯奈德\n※普利策诗歌奖获奖作品\n※把历史和荒野都记在心里，让诗接近事物的真实尺度，以对抗我们这个时代... ', '2018-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('张枣的诗', 9200, '\n\n\n张枣', '随笔', '人民文学出版社', '这本诗集首次结集了张枣迄今为止所见的全部诗歌作品130多首，展示了这位当代最“低产”的杰出诗人的全部诗歌面貌。其中首次收集了诗人《镜中》之前的许多作品，给读... ', '2021-02-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我将敢于亲吻你', 9201, '\n\n\n（阿根廷）阿方斯娜·斯托尔妮汪天艾', '随笔', '译林出版社', '她是20世纪最突出的“现代女性主义”声音，\n她是“百年一遇的诗人”，\n她是坊间传唱多年的歌曲，\n她是电影孤独和悲剧的主题，\n她颂扬女性爱欲的觉醒，\n她主张女... ', '2016-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('取瑟而歌', 9202, '\n\n\n张定浩', '随笔', '华东师范大学出版社', '《取瑟而歌：如何理解新诗》通过分析林徽因、穆旦、顾城、海子、马雁等几位优秀汉语诗人及其诗歌，提供理解新诗的有效路径，希图使读者面对一首陌生的诗时不再胆怯和无... ', '2017-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('恰尔德·哈洛尔德游记', 9203, '\n\n\n[英]乔治·戈登·拜伦杨熙龄', '随笔', '广西师范大学出版社', '《恰尔德·哈洛尔德游记》不仅是英国浪漫主义文学中一部伟大的自传体长诗，也是一种厌世情绪的真实写照，之所以这样，是因为拜伦以及同代英国文人对法国大革命和拿破仑... ', '2005-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('死于黎明：洛尔迦诗选', 9204, '\n\n\n[西]费德里科•加西亚•洛尔迦', '随笔', '华东师范大学出版社', '洛尔迦是西班牙著名诗人，他把安达卢西亚民间抒情谣曲的韵律、现代诗歌的技艺和超现实主义的想象力完美地结合起来，被视为西班牙“二七年一代”诗人的杰出代表。\n今年... ', '2020-07-25', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('未知大学', 9205, '\n\n\n[智利]罗贝托·波拉尼奥范晔', '随笔', '上海人民出版社', '波拉尼奥一直以诗人自居，非常珍视自己的诗作，得知病情的几个月后，他亲自整理自己几乎所有的诗歌手稿，是未来将出版的《未知大学》的雏形。\n中文版诗集以《未知大学... ', '2019-02-25', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('给青年诗人的信', 9206, '\n\n\n[奥]莱内·马利亚·里尔克冯至', '随笔', '上海译文出版社', '《给青年诗人的信》，辑录里尔克在一九○三至一九○八年间写给渴望成为诗人的青年卡卜斯的十封信。这些信是里尔克对创作的思考，更是对艰难、寂寞、爱等人生问题的解答... ', '2009-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('在爱的废墟上', 9207, '\n\n\n[加拿大]露比·考尔程婧波', '随笔', '上海文艺出版社', '这是一本关于自我疗愈的书，\n一本“心灵蜕变日记”，\n一本迷人诗集。\n--------------------------------------------... ', '2017-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('观看王维的十九种方式', 9208, '\n\n\n[美国]艾略特.温伯格、', '随笔', '商务印书馆', '◆入选 豆瓣·2019年外国文学非小说类年度十佳\n◆编辑推荐\n有关汉语诗歌翻译的经典之作。\n当今昔皆为汉语的一首诗，变成一首英语诗、一首西语诗、一首法语诗，... ', '2015-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('巴黎的忧郁', 9209, '\n\n\n[法]夏尔·波德莱尔郭宏安', '随笔', '上海译文出版社', '本书收录《巴黎的忧郁》、《人造天堂》、《私人日记》三部作品，是波德莱尔的散文诗代表作。\n波德莱尔是第一个把散文诗当作一种独立的形式并使之趋于完善的人，提出了... ', '2020-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('一只狼在放哨', 9210, '\n\n\n[伊朗]阿巴斯·基阿鲁斯达米黄灿然', '随笔', '中信出版集团', '阿巴斯不仅是一位备受瞩目电影导演，他的电影被称为“诗意电影”，因为阿巴斯的另外一个身份是一位风格独特的诗人。事实上，他的诗歌写作比其他的艺术行为在时间上要早... ', '2013-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('致未来的诗人', 9211, '\n\n\n[西班牙]路易斯·塞尔努达范晔', '随笔', '华东师范大学出版社', '然而我不在乎无人了解\n在这些近乎同代的身体之间，\n他们活着的方式不像我\n这来自疯狂土地的身体\n挣扎着成为翅膀抵达空间之墙\n是那墙壁将我的岁月与你的未来相隔。... ', '2020-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('灵光集', 9212, '\n\n\n[法]阿蒂尔·兰波何家炜', '随笔', '商务印书馆', '·“该我了。且听我种种疯狂故事中的一个。”——法国著名诗人兰波经典作品\n象征派代表人物、超现实主义鼻祖、通灵诗人兰波，在现代诗歌史上有着不可替代的地位。他自... ', '2006-02-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('在春天走进果园', 9213, '\n\n\n鲁米梁永安', '随笔', '甘肃人民美术出版社', '鲁米被联合国评价为“属于整个人类的最伟大的人文主义者、哲学家和诗人”。本书是鲁米这位大彻大悟的圣哲的心灵语录。鲁米的诗歌击穿事物的表象，直达灵魂深处。对灵性... ', '2017-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('云雀叫了一整天', 9214, '\n\n\n木心', '随笔', '上海三联书店', '内容简介\n“从前的日色变得慢，车、马、邮件都慢，一生只够爱一个人……”\n木心诗集《云雀叫了一整天》，依据木心晚年定稿，甲辑收入《大心情》《火车中的情诗》《永... ', '2017-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('影响的焦虑', 9215, '\n\n\n哈罗德•布鲁姆徐文博', '随笔', '江苏教育出版社', '本书从精神分析学的角度研究诗人对诗人的影响。作者认为经典树立起了一个不可企及的高度，诗的历史形成乃是一代代诗人误读各自前驱的结果。通过运用弗洛伊德的家庭罗曼... ', '2019-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('天狼星的阴影', 9216, '\n\n\n(美)W.S.默温曾虹', '随笔', '人民文学出版社', '透过今秋的云光\n看见我最初来的山谷\n我半生以前\n哦大半生以前的河流\n映照手掌形的天空\n从来既非已知也非未知\n从来既非属于我 也非不属于我\n在天以外的远方\n山... ', '2013-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('白石上的黑石', 9217, '\n\n\n[秘鲁]塞萨尔·巴列霍陈黎、张芬龄', '随笔', '外语教学与研究出版社', '苦难根茎上结出的奇异果实  孤独灵魂一生的漂泊与挣扎\n陈黎、张芬龄译诗生涯最早致力和最钟爱的拉美现代诗人\n《白石上的黑石》选辑秘鲁诗人巴列霍代表作品70首，... ', '2011-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('从前的我也很可爱啊', 9218, '\n\n\n[日]石川啄木周作人', '随笔', '北京时代华文书局', '寂寞的时候，只能念一首石川啄木。\n石川啄木是天才诗人，其诗歌有清澈的忧伤，又有明快的可爱。读一首石川啄木，能读出轻轻的寂寞，也能感受淡淡的可爱。\n《从前的我... ', '2009-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('水的空白', 9219, '\n\n\n[罗]马林·索雷斯库高兴', '随笔', '上海人民出版社', '索雷斯库是罗马尼亚著名的先锋诗人，他是以反叛者的姿态登上罗马尼亚诗坛的，因他的写法完全有悖于传统，所以评论界称他的诗是“反诗”。他的诗让人感觉亲切和自然，亲... ', '2013-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('兰波作品全集', 9220, '\n\n\n[法]兰波王以培', '随笔', '作家出版社', '这是法兰西通灵诗人兰波的作品全集，其中包括他的全部诗歌、散文诗和部分书信、日记，是目前为止，国内翻译出版的最完整的一部兰波作品全集。诗人兰波（Arthur ... ', '2017-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('时间的玫瑰', 9221, '\n\n\n北岛', '随笔', '江苏文艺出版社', '本书中不少片断是在路上写成的。从委内瑞拉山区的小旅店到马其顿湖边的酒吧，从柏林出发的夜行火车到等候转机的芝加哥机场。正是这种跨国旅行，与诗人写作中的越界有对... ', '2016-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('神曲', 9222, '\n\n\n[意大利]但丁朱维基', '随笔', '上海译文出版社', '《神曲》采用了中世纪流行的梦幻文学的形式，描写了一个幻游地狱、炼狱、天堂三界的故事。全诗三卷，分别为《地狱》、《炼狱》和《天堂》，每卷三十三篇，加上序共一百... ', '2010-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('爱是地狱冥犬', 9223, '\n\n\n[美]布考斯基徐淳刚', '随笔', '华东师范大学出版社', '国内首部布考斯基版权诗集\n我始终一手拿着酒瓶，一面注视着人生的曲折、打击与黑暗……对我而言，生存，就是一无所有地活着。\n——布考斯基\n华兹华斯、惠特曼、威廉... ', '2021-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('路邊野餐', 9224, '\n\n\n毕赣', '随笔', '前景娱乐有限公司', '《路邊野餐》金馬獎新導演畢贛，為首部電影長片，限量推出的同名詩集。\n電影中的主人翁陳升，住在貴州黔東南的凱里縣城，一座神秘潮濕的亞熱帶鄉土，身為小診所醫生，... ', '2003-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('顾城的诗', 9225, '\n\n\n顾城', '随笔', '人民文学出版社', '《蓝星诗库（金版）：顾城的诗》主要内容包括：星月的来由·烟囱、对宇宙大声发问、我的幻想·美、怀念、铭言（二首）、割草谣、找寻、无名的小花、幼芽、小风景、我赞... ', '2020-09-01', -1, 30000, '泰山');
