INSERT INTO `books` VALUES ('在黑暗的河流上', 9227, '\n\n\n席慕蓉', '随笔', '南海出版公司', '当灯火逐盏熄灭，在黑暗的河流上被你听遗落了的一切，终于，只能成为，你的昔日，我的昨夜…… ', '2018-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('欧丽娟品读古诗词', 9228, '\n\n\n欧丽娟', '随笔', '北京联合出版公司', '本书是一本诗歌赏析书，也是一本中国诗歌史。从神话、诗经、楚辞、乐府、唐诗、宋诗跨越七个时代，解读47位诗人，涵盖20个主题，旁征博引200余首诗歌。帮我们厘... ', '2017-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('诗艺', 9229, '\n\n\n[阿根廷]博尔赫斯陈重仁', '随笔', '上海译文出版社', '演讲集。一九六七年秋，博尔赫斯应哈佛大学诺顿讲座之邀，就诗的地位、隐喻模式、小说与诗、音韵与翻译等展开六讲。讲座录音带在图书馆尘封三十多年后，由时任西安大略... ', '2014-01-01', -1, 30000, '泰山');
