INSERT INTO `books` VALUES ('尼采诗集', 9239, '\n\n\n[德]弗里德里希·尼采', '随笔', '上海译文出版社', '尼采对格言艺术下了千锤百炼的功夫。有些格言诗，短短两行，构思之巧妙，语言之质朴，意味之深长，堪称精品。\n尼采的抒情诗节奏跳跃，韵律自由，如同在崎岖山中自由舞... ', '2018-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('吉檀迦利', 9240, '\n\n\n[印度]泰戈尔萧兴政', '随笔', '云南人民出版社', '叶芝读时默默流泪。纪德说“没有任何诗集能像《吉檀迦利》一样给我以灵魂上的震撼”。\n《吉檀迦利》是亚洲首部诺贝尔文学奖获奖诗集，共103首。整部诗集，在情感与... ', '2019-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('唐诗可以这样读', 9241, '\n\n\n欧丽娟', '随笔', '浙江人民出版社', '唐诗不仅是诗歌史上一座巍峨的高峰，亦是人类文明的珍贵献礼。\n本书的目的并非对诗人、诗歌作一般性的概论，而是以专题的方式抽绎由初唐到晚唐诗歌的许多面向，除了厘... ', '2018-03-01', -1, 30000, '泰山');
