INSERT INTO `books` VALUES ('木心诗选', 9245, '\n\n\n木心', '随笔', '广西师范大学出版社', '《木心诗选》的诗歌，共七辑（158首），从最早期的《阿里山之夜》，到最为读者所知的《从前慢》，以及最最“天书”的《同袍》四言诗等，各个时期的，均精选于木心生... ', '2020-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('楚辞', 9246, '\n\n\n屈原等林家骊注释', '随笔', '中华书局', '《楚辞》内容简介：当我们穿越时空来到战国时代的时候，我们无法不为这个时代绚丽辉煌的文化所惊叹、所倾倒。那正是被德国哲学家雅斯贝尔斯称为“轴心时代”的人类文明... ', '2015-01-01', -1, 30000, '泰山');
