INSERT INTO `books` VALUES ('一百万亿首诗', 9248, '\n\n\n[法]雷蒙·格诺吴燕南', '诗歌', '中信出版集团', '《一百万亿首诗》由十首十四行诗构成。十首诗除严格遵循经典十四行诗的格律和规范外，各首诗的同一行还押相同的韵。在作品的成书形式上，十首诗都单面排印，各行之间被... ', '2015-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('杨牧诗选 1956-2013', 9249, '\n\n\n杨牧', '诗歌', '广西师范大学出版社', '朝向一首诗的完成，Towards the Completion of a Poem\n1956—1966，1966—1976，1976—1986，1986—1... ', '2011-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('晚唐钟声', 9250, '\n\n\n傅道彬', '诗歌', '北京大学出版社', '《晚唐钟声:中国文学的原型批评(修订本)》是运用原型批评方法阐述中国文化与文学象征意蕴的学术专著。原型是一部凝缩和积淀了的人类历史和文化历史，原型批评是一种... ', '1999-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('雪山短歌', 9251, '\n\n\n马骅世纪文景', '诗歌', '上海人民出版社', '《雪山短歌》与马骅之前的出版物不同，由两大部分组成，一部分是马骅的遗作（40首诗歌、7封书信），一部分是友人针对他的创作的解析、回忆文章，而且，所有内容均只... ', '2020-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('济慈诗选', 9252, '\n\n\n济慈屠岸', '诗歌', '外语教学与研究出版社', '《英诗经典名家名译:济慈诗选(英汉对照)》内容简介：济慈是英国浪漫主义诗歌的巨擘，他遍涉各种诗歌体裁，经历几次诗风的变化，完成了一系列惊世的杰作。《英诗经典... ', '2003-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('唐诗鉴赏辞典', 9253, '\n\n\n萧涤非、等', '诗歌', '上海辞书出版社', '《唐诗鉴赏辞典》旨在介绍唐诗之精华。它搜集了一百九十多位诗人的一千余篇作品，出自大家、名家之手，流传万口的名篇，固然都在网罗之列；同时，也选采了许多不见录于... ', '2007-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('乱发', 9254, '\n\n\n[日]与谢野晶子尤海燕', '诗歌', '北京联合出版公司', '✨周作人、石川啄木一致推崇，深刻影响后世女性文学创作\n✨日本知名文艺评论家田边圣子赞她为“一千年才出一个”的天才女诗人\n✨与谢野晶子首部简体个人短歌精选集\n... ', '2020-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('致橡树', 9255, '\n\n\n舒婷', '诗歌', '江苏文艺出版社', '第一辑 双桅船\n致大海\n海滨晨曲\n珠贝——大海的眼泪\n船\n呵，母亲\n赠\n当你从我的窗下走过\n中秋夜\n心愿\n悼\n致橡树\n自画像\n四月的黄昏\n思念\n往事二三\n祖... ', '2018-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我纷纷的情欲', 9256, '\n\n\n木心', '诗歌', '广西师范大学出版社', '《我纷纷的情欲》是木心的诗集。 ', '2016-08-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('诗人的思考', 9257, '\n\n\n[美]海伦·文德勒刘晗', '诗歌', '浙江大学出版社', '本书涉及的四位诗人——亚历山大•蒲柏、沃尔特•惠特曼、艾米莉•狄金森和威廉•巴特勒•叶芝——来自三个世纪和三个国家，他们的思维方式具有独特的特点。针对将抒情... ', '2018-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('夜颂', 9258, '\n\n\n[德]诺瓦利斯林克', '诗歌', '四川人民出版社', '《夜颂》收录了作者的诗歌和断片。断片是诺瓦利斯作品中的精华，集中体现了他惊人的、与众不同的哲思妙语。这些内同涉猎数学与自然科学、医学与灵魂学、语言学与艺术、... ', '2018-10-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我坐在火山的最边缘', 9259, '\n\n\n包慧怡', '诗歌', '河南大学出版社', '本书收录了青年学者、诗人包慧怡2005—2015年间的代表诗作，是作者第一本正式出版的诗歌集。\n包慧怡的诗具有一种罕见的女性气质，它不同于诗歌角色中的女性意... ', '2003-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('夜空总有最大密度的蓝色', 9260, '\n\n\n[日]最果夕日匡匡', '诗歌', '世纪文景|上海人民出版社', '☆震惊日本文坛、演艺圈的神秘诗人，日本现象级畅销诗集登陆！\n☆《编舟记》导演石井裕也操刀同名电影，获《电影旬报》2017年年度十佳第一名。\n☆日本国民诗人谷... ', '1997-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('我的焦虑是一束火花', 9261, '\n\n\n[叙利亚]阿多尼斯薛庆国', '诗歌', '译林出版社', '这是享誉世界诗坛的阿拉伯大诗人阿多尼斯的诗歌短章选集。\n阿多尼斯擅写长诗，也珍视自己的短章：“短章是闪烁的星星，燃烧的蜡烛；长诗是尽情流溢的光明，是史诗的灯... ', '1982-02-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('北岛诗歌集', 9262, '\n\n\n北岛', '诗歌', '南海出版公司', '卑鄙是卑鄙者的通行证，高尚是高尚者的墓志铭。动荡不安的世界里，诗人的喊声永远是最响亮的，他们的眼睛如锐利的手术刀剥去了世俗的外衣，直入世间万物的本质。 ', '2013-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('海子诗全编', 9263, '\n\n\n海子、西川编', '诗歌', '三联书店上海分店', '海子（1964-1989），原名查海生。出版的诗集有《土地》（1990）、《海子、骆一禾作品集》（1991）、《海子的诗》（1995）、《海子诗全编》（19... ', '2004-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('拜伦诗选', 9264, '\n\n\n拜伦查良铮', '诗歌', '上海译文出版社', '唐璜》（节选）\r\n\r\n\r\n第一章（节选）\r\n\r\n     （寻找英雄人物）\r\n\r\n        一\r\n\r\n说来新鲜，我苦于没有英雄可写，\r\n尽管当今之世，... ', '2015-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('被禁锢的头脑', 9265, '\n\n\n(波兰)切斯瓦夫·米沃什乌兰', '诗歌', '广西师范大学出版社', '本书是1980年度诺贝尔文学奖获得者米沃什写于1950年代初的经典作品，对于二战前后波兰以及波罗的海三国人的处境做了精彩的描述与反省。米沃什的许多真知灼见放... ', '2017-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('初唐诗', 9266, '\n\n\n（美）宇文所安贾晋华', '诗歌', '三联书店', '《初唐诗》《盛唐诗》三联版序言\n英文版的《初唐诗》和《盛唐诗》大约是在四分之一个世纪以前出版的。那时的中国古典文学学术界和现在十分不同。那时，我们拥有基本的... ', '1994-11-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('爱丽尔', 9267, '\n\n\n[美]西尔维娅·普拉斯包慧怡', '诗歌', '南海出版公司', '《爱丽尔》是美国著名诗人普拉斯的代表诗歌集。她在1963年2月11日自杀时，在书桌上留下了一个黑色的弹簧活页夹，里面是四十首诗作的手稿合集，集子最初的起名是... ', '2013-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('嘿，小家伙', 9268, '\n\n\n温酒', '诗歌', '湖南文艺出版社', '“我走了很远很远，从不问归期。 但即便走了几千里，你也在我心里。”\n谁说长大了就不能读童话？谁说成人了就没有童话看？每个人心里都有一个永远长不大的孩子。\n世... ', '2004-05-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('格林童话全集', 9269, '\n\n\n[德国]格林兄弟魏以新', '诗歌', '人民文学出版社', '《格林童话全集》是德国民间故事集。由德国的雅各・格林和威廉・格林兄弟根据民间口述材料改写而成。他们搜集、整理、出版这些民间童话的目的，正如威廉所说，是\"希望... ', '2009-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('小王子', 9270, '\n\n\n安托万•德•圣埃克苏佩里李继宏', '诗歌', '天津人民出版社', '《小王子》是一本足以让人永葆童心的不朽经典，被全球亿万读者誉为非常值得收藏的书。\n遥远星球上的小王子，与美丽而骄傲的玫瑰吵架负气出走，在各星球漫游中，小王子... ', '2003-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('夏洛的网', 9271, '\n\n\n[美]E.B.怀特任溶溶', '诗歌', '上海译文出版社', '一个蜘蛛和小猪的故事，写给孩子，也写给大人。\n在朱克曼家的谷仓里，快乐地生活着一群动物，其中小猪威尔伯和蜘蛛夏洛建立了最真挚的友谊。然而，一个最丑恶的消息打... ', '2003-06-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('小王子', 9272, '\n\n\n[法]圣埃克絮佩里周克希', '诗歌', '上海译文出版社', '&nbsp;&nbsp;&nbsp;&nbsp;《小王子》是法国著名作家圣埃克絮佩里的一部享誉世界的畅销童话。本书是中、法、英三种语言对照的版本，采用彩色插... ', '2015-04-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('一千零一夜', 9273, '\n\n\n纳训', '诗歌', '人民文学出版社', '《一千零一夜》被高尔基誉为\"世界民间文学史上最壮丽的一座纪念碑\"。本书故事精彩、妙趣横生、想象丰富、扣人心弦，充满神秘奇幻的色彩，蕴藏瑰丽丰富的风貌，既是儿... ', '2003-01-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('王尔德童话', 9274, '\n\n\n[英]王尔德王林', '诗歌', '译林出版社', '王尔德是19世纪英国最伟大的艺术家之一，以其剧作、诗歌、童话和小说名世。在风流才子那颓废唯美、狷狂放浪的表面姿态下，是一颗纯美纯善，永难泯灭的童心。而这可贵... ', '2012-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('夜莺与玫瑰', 9275, '\n\n\n[英]奥斯卡·王尔德谈瀛洲', '诗歌', '浙江文艺出版社', '除了《小王子》，成人童话我们还看王尔德《夜莺与玫瑰》。\n第一部真正写给成人看的童话，他第一个提出成人童话概念。\n王尔德说：“童话不是为儿童而写，是为十八到八... ', '2007-07-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('绿山墙的安妮', 9276, '\n\n\n[加]露西·莫德·蒙哥玛利马爱农', '诗歌', '浙江文艺出版社', '一部最甜蜜的描写儿童生活的小说。安妮是个身世凄凉的小女孩，出生不久即父母双亡，成了孤儿。好心的邻居把她养到六岁之后她又到另一户人家看孩子，后来被送到了孤儿院... ', '1999-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('柳林风声', 9277, '\n\n\n[英]肯尼斯·格雷厄姆', '诗歌', '中国城市出版社', '这是一个爸爸写给儿子的童话，是一个适合围坐在暖暖的火炉边，大家一起听的故事。\n书中以细腻典雅的笔调描绘了大自然风光的诗意变化，被誉为“英文散文体作品的典范”... ', '2011-09-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('快乐王子', 9278, '\n\n\n[英]王尔德巴金', '诗歌', '上海译文出版社', '这本书收入王尔德两部著名的童话集《快乐王子集》和《石榴之家》，是唯美主义童话的代表作。作家除遵循一般童话中应有的惩恶扬善、锄强扶弱、劫富济贫以及褒美贬丑等主... ', '2003-12-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('长袜子皮皮', 9279, '\n\n\n[瑞典]阿斯特丽德·林格', '诗歌', '中国少年儿童出版社', '一位小姑娘一个人住在一栋小房子里，生活完全自理，富得像一位财神，壮得像一匹马，他的爸爸是南海一个岛上的国王。她所做的一切几乎都违背了成年人的意志，不去学校上... ', '1999-03-01', -1, 30000, '泰山');
INSERT INTO `books` VALUES ('安吉拉·卡特的精怪故事集', 9280, '\n\n\n[英]安吉拉·卡特郑冉然', '诗歌', '南京大学出版社', '很久很久以前，精怪故事不光是给孩子们看的，《安吉拉·卡特的精怪故事集》就是如此。这部精彩的集子囊括了抒情故事、血腥故事、令人捧腹的故事和粗俗下流的故事，它们... ', '2011-09-01', -1, 30000, '泰山');
